-- ----------------------------
-- Table structure for books
-- ----------------------------
DROP TABLE IF EXISTS `books`;
CREATE TABLE `books`  (
  `bookId` int(11) NOT NULL AUTO_INCREMENT,
  `bookName` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `auth` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `tinyDescription` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `language` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `year` int(11) NULL DEFAULT NULL,
  `nxb` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `price` int(11) NULL DEFAULT NULL,
  `quantity` int(11) NULL DEFAULT NULL,
  `subCatId` int(11) NULL DEFAULT NULL,
  `sale` float NULL DEFAULT 1,
  `coverImg` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `thumbnails` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `isDisable` tinyint(4) NULL DEFAULT 0,
  `createAt` datetime(0) NULL DEFAULT current_timestamp(0),
  `updateAt` datetime(0) NULL DEFAULT current_timestamp(0) ON UPDATE CURRENT_TIMESTAMP(0),
  PRIMARY KEY (`bookId`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 44 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of books
-- ----------------------------
INSERT INTO `books` VALUES (1, 'Sống Mòn', 'Nam Cao', 'Một bản thảo được nhà văn Nam Cao hoàn thành từ trước Cách mạng nhưng phải đợi tới sau ngày hòa bình lập lại ở miền Bắc cuốn tiểu thuyết mới được ra mắt.', 'Mặc dù bản thảo Sống mòn đã được nhà văn Nam Cao hoàn thành từ trước Cách Mạng nhưng phải đợi tới sau ngày hòa bình lập lại ở miền Bắc cuốn tiểu thuyết mới lần đầu ra mắt độc giả.\r\n\r\nCó lẽ là vì thế, vì được xuất bản khi thời đại đã chuyển nên Sống mòn như bị lỡ mất nhịp, không làm thành một sự kiện lớn trong dư luận văn học hồi bấy giờ. Rồi liên tiếp sau đó là những biến động lớn lao và không ngừng cho tới tận năm 1975 của đất nước khiến cuốn tiểu thuyết có chiều nội tâm quá sâu và quá nặng ưu tư ấy vốn đã không thể có đông người đọc càng ít người đọc hơn, vốn đã quá kín đáo và kín tiếng lại càng bị chìm tiếng đi giữa một thời đại văn học bừng bừng hào khí và đầy những tiếng động vang dội.\r\n\r\nNgày nay, Sống mòn đã được nhắc tới nhiều hơn, nhưng vẫn có vẻ là được nhắc tới một cách lớt phớt. Dường như chính tầm cỡ những truyện ngắn của Nam Cao đã tạo nên một định kiến rằng Nam Cao là tác gia truyện ngắn, sự nghiệp để đời bằng truyện ngắn, chỉ truyện ngắn.', 'Tiếng Việt', 2016, 'NXB Văn Học', 69000, 291, 1, 0.78, 'chuyencotichchonguoilon_thumb1.jpg', 'songmon_thumb1.jpg', 0, '2022-05-24 10:31:35', '2022-06-01 22:58:52');
INSERT INTO `books` VALUES (2, 'Mắt Biếc (Tái Bản 2019)', 'Nguyễn Nhật Ánh', 'Mắt Biếc (Tái Bản 2019)', 'Mắt biếc là một tác phẩm được nhiều người bình chọn là hay nhất của nhà văn Nguyễn Nhật Ánh. Tác phẩm này cũng đã được dịch giả Kato Sakae dịch sang tiếng Nhật để giới thiệu với độc giả Nhật Bản.\r\n\r\n“Tôi gửi tình yêu cho mùa hè, nhưng mùa hè không giữ nổi. Mùa hè chỉ biết ra hoa, phượng đỏ sân trường và tiếng ve nỉ non trong lá. Mùa hè ngây ngô, giống như tôi vậy. Nó chẳng làm được những điều tôi ký thác. Nó để Hà Lan đốt tôi, đốt rụi. Trái tim tôi cháy thành tro, rơi vãi trên đường về.”\r\n\r\n… Bởi sự trong sáng của một tình cảm, bởi cái kết thúc buồn, rất buồn khi xuyên suốt câu chuyện vẫn là những điều vui, buồn lẫn lộn… ', 'Tiếng Việt', 2019, 'NXB Trẻ', 110000, 300, 1, 0.85, NULL, 'matbiet_thumb1.jpg', 0, '2022-05-25 01:27:55', '2022-05-27 21:28:29');
INSERT INTO `books` VALUES (3, 'Hạ Đỏ (Tái Bản 2018)', 'Nguyễn Nhật Ánh', 'Kể về mối tình đầu trong sáng của một cậu học trò dành cho một cô gái', 'Kể về mối tình đầu trong sáng của một cậu học trò dành cho một cô gái quê 16 tuổi trong dịp về quê nghỉ hè, và những việc làm cao đẹp của cậu cho em bé quê chân chất chịu nhiều thiệt thòi ở nông thôn. Chuyện nhiều hình ảnh, dễ thương và trong sáng… vẫn không thiếu những “pha” thú vị cho ta những tiếng cười sảng khoái.', 'Tiếng Việt', 2018, 'NXB Trẻ', 60000, 300, 1, 0.82, NULL, 'hado_thumb1.jpg', 0, '2022-05-25 01:32:20', '2022-05-27 19:22:04');
INSERT INTO `books` VALUES (4, 'Ngày Xưa Có Một Chuyện Tình (Tái Bản 2019)', 'Nguyễn Nhật Ánh', 'NGÀY XƯA CÓ MỘT CHUYỆN TÌNH là tác phẩm mới tinh thứ 2 trong năm 2016 của nhà văn Nguyễn Nhật Ánh', 'NGÀY XƯA CÓ MỘT CHUYỆN TÌNH là tác phẩm mới tinh thứ 2 trong năm 2016 của nhà văn Nguyễn Nhật Ánh dài hơn 300 trang, được coi là tập tiếp theo của tập truyện Mắt biếc. Có một tình yêu dữ dội, với em,  của một người yêu em hơn chính bản thân mình - là anh.\r\n\r\nNgày xưa có một chuyện tình có phải là một câu chuyện cảm động  khi người ta yêu nhau, nỗi khát khao một hạnh phúc êm đềm ấm áp đến thế; hay đơn giản chỉ là chuyện ba người - anh, em, và người ấy…?\r\n\r\nBạn hãy mở sách ra, để chứng kiến làn gió tình yêu chảy qua như rải nắng trên khuôn mặt mùa đông của cô gái; nụ hôn đầu tiên ngọt mật, cái ôm đầu tiên, những giọt nước mắt và cái ôm xiết cuối cùng… rồi sẽ tìm thấy câu trả lời, cho riêng mình.', 'Tiếng Việt', 2019, 'NXB Trẻ', 125000, 295, 1, 0.85, NULL, 'ngayxuacomotchuyentinh_thumb1.jpg', 0, '2022-05-25 01:35:42', '2022-05-27 20:00:04');
INSERT INTO `books` VALUES (5, 'Chuyện Cổ Tích Dành Cho Người Lớn (Tái Bản 2018)', 'Nguyễn Nhật Ánh', 'Một bộ sưu tập truyện cười', '\r\n“Chuyện cổ tích dành cho người lớn” của tác giả Nguyễn Nhật Ánh là một đầu sách nổi bật giữa các đầu sách nổi tiếng của ông, không viết về trẻ thơ, về tình yêu của các cô cậu học trò, hay các anh chị đôi mươi mới lớn, mà viết về những phút giây đời thường của những con người “lớn” khi đã kết hôn, …. Là những tập truyện ngắn hài hước, nhưng sâu trong đó cũng có nhỏ nhẹ những triết lí đạo làm vợ chồng, … Từng mẩu chuyện nhỏ, đọc nó lên ta cảm nhận được hơi ấm tình người, những trò đùa vặt, giận dỗi hờn ghen trong đó làm cho con người ta thích thú. Các tình huống được dựng lên một cách rất đời thường, dưới ngòi bút tài ba, kì cựu của tác giả Nguyễn Nhật Ánh, mọi thứ dù yêu hay ghét đều hiện lên rất chân thực.  ', 'Tiếng Việt', 2018, 'NXB Trẻ', 52000, 300, 1, 0.84, 'chuyencotichchonguoilon_thumb1.jpg', 'chuyencotichchonguoilon_thumb1.jpg', 0, '2022-05-25 01:39:06', '2022-06-01 12:52:04');
INSERT INTO `books` VALUES (29, 'Tư Duy Nhanh Và Chậm', '	Daniel Kahneman', NULL, 'Thinking fast and slow - Tư duy nhanh và chậm  - cuốn sách được Tạp chí Tài chính Mỹ đặc biệt đánh giá là “một kiệt tác” trong việc nói về tính hợp lý, phi lý của con người trong tư duy, trong việc đánh giá và ra quyết định.\r\n\r\nTrong cuộc sống, dù bạn có cẩn trọng đến mức nào thì vẫn có những lúc bạn đưa ra những quyết định dựa trên cảm tình chủ quan của mình. Thinking fast and slow, cuốn sách nổi tiếng tổng hợp tất cả nghiên cứu được tiến hành qua nhiều thập kỷ của nhà tâm lý học từng đạt giải Nobel Kinh tế Daniel Kahneman sẽ cho bạn thấy những sư hợp lý và phi lý trong tư duy bản thân. Cuốn sách được đánh giá là “kiệt tác” trong việc thay đổi hành vi của con người, Thinking fast and slow đã giành được vô số giải thưởng danh giá, lọt vào Top 11 cuốn sách kinh doanh hấp dẫn nhất năm 2011. Cuốn sách được Alpha Books mua bản quyền và xuất bản vào Quý I năm nay. Thinking fast and slow dù là cuốn sách có tính hàn lâm cao nhưng được truyền tải một cách vui nhộn và dễ hiểu, hứa hẹn sẽ mang lại cho bạn đọc nhiều kiến thức mới mẻ, bổ ích.\r\n\r\nThinking fast and slow sẽ đưa ra và lý giải hai hệ thống tư duy tác động đến con đường nhận thức của bạn. Kahneman gọi đó là: hệ thống 1 và hệ thống 2. Hệ thống 1, còn gọi là cơ chế nghĩ nhanh, tự động, thường xuyên được sử dụng, cảm tính, rập khuôn và tiềm thức. Hệ thống 2, còn gọi là cơ chế nghĩ chậm, đòi hỏi ít nỗ lực, ít được sử dụng, dùng logic có tính toán và ý thức. Trong một loạt thí nghiệm tâm lý mang tính tiên phong, Kahneman và Tversky chứng minh rằng, con người chúng ta thường đi đến quyết định theo cơ chế nghĩ nhanh hơn là ghĩ chậm. Phần lớn nội dung của cuốn sách chỉ ra những sai lầm của con người khi suy nghĩ theo hệ thống 1. Kahneman chứng minh rằng chúng ta tệ hơn những gì chúng ta tưởng: đó là chúng ta không biết những gì chúng ta không biết!\r\n\r\nThinking fast and slow đáp ứng hai tiêu chí của một cuốn sách hay, thứ nhất nó thách thức quan điểm của người đọc, thứ hai, nó không phải là những trang sách với những con chữ khô cứng mà vô cùng vui nhộn và hấp dẫn. Không nghi ngờ gì nữa, đây là cuốn sách hàn lâm dành cho tất cả mọi người!\r\n\r\nVề tác giả:\r\n\r\nDaniel Kahneman, sinh năm 1934, nhà tâm lý học người Mỹ  gốc Israel đã dành giải Nobel Kinh tế năm 2002. Ông dành cả cuộc đời của mình để nghiên cứu về tâm lý học hành vi con người. Cùng với người cộng sự đã qua đời Amos Tversky, hai ông đã có được những nghiên cứu sâu sắc về con đường tư duy và nhận thức của con người.\r\n\r\nMã hàng	8935251416930\r\nTên Nhà Cung Cấp	Alpha Books\r\nTác giả	Daniel Kahneman\r\nNgười Dịch	Hương Lan-Xuân Thanh\r\nNXB	NXB Thế Giới\r\nNăm XB	2021\r\nTrọng lượng (gr)	350\r\nKích Thước Bao Bì	24 x 16 cm\r\nSố trang	612\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nAlpha Books\r\nFlashsale\r\nMã Giảm Giá\r\nTủ Sách Tâm Lý Kỹ Năng\r\nVNPAY\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Kỹ năng sống bán chạy của tháng\r\nThinking fast and slow - Tư duy nhanh và chậm  - cuốn sách được Tạp chí Tài chính Mỹ đặc biệt đánh giá là “một kiệt tác” trong việc nói về tính hợp lý, phi lý của con người trong tư duy, trong việc đánh giá và ra quyết định.\r\n\r\nTrong cuộc sống, dù bạn có cẩn trọng đến mức nào thì vẫn có những lúc bạn đưa ra những quyết định dựa trên cảm tình chủ quan của mình. Thinking fast and slow, cuốn sách nổi tiếng tổng hợp tất cả nghiên cứu được tiến hành qua nhiều thập kỷ của nhà tâm lý học từng đạt giải Nobel Kinh tế Daniel Kahneman sẽ cho bạn thấy những sư hợp lý và phi lý trong tư duy bản thân. Cuốn sách được đánh giá là “kiệt tác” trong việc thay đổi hành vi của con người, Thinking fast and slow đã giành được vô số giải thưởng danh giá, lọt vào Top 11 cuốn sách kinh doanh hấp dẫn nhất năm 2011. Cuốn sách được Alpha Books mua bản quyền và xuất bản vào Quý I năm nay. Thinking fast and slow dù là cuốn sách có tính hàn lâm cao nhưng được truyền tải một cách vui nhộn và dễ hiểu, hứa hẹn sẽ mang lại cho bạn đọc nhiều kiến thức mới mẻ, bổ ích.\r\n\r\nThinking fast and slow sẽ đưa ra và lý giải hai hệ thống tư duy tác động đến con đường nhận thức của bạn. Kahneman gọi đó là: hệ thống 1 và hệ thống 2. Hệ thống 1, còn gọi là cơ chế nghĩ nhanh, tự động, thường xuyên được sử dụng, cảm tính, rập khuôn và tiềm thức. Hệ thống 2, còn gọi là cơ chế nghĩ chậm, đòi hỏi ít nỗ lực, ít được sử dụng, dùng logic có tính toán và ý thức. Trong một loạt thí nghiệm tâm lý mang tính tiên phong, Kahneman và Tversky chứng minh rằng, con người chúng ta thường đi đến quyết định theo cơ chế nghĩ nhanh hơn là ghĩ chậm. Phần lớn nội dung của cuốn sách chỉ ra những sai lầm của con người khi suy nghĩ theo hệ thống 1. Kahneman chứng minh rằng chúng ta tệ hơn những gì chúng ta tưởng: đó là chúng ta không biết những gì chúng ta không biết!\r\n\r\nThinking fast and slow đáp ứng hai tiêu chí của một cuốn sách hay, thứ nhất nó thách thức quan điểm của người đọc, thứ hai, nó không phải là những trang sách với những con chữ khô cứng mà vô cùng vui nhộn và hấp dẫn. Không nghi ngờ gì nữa, đây là cuốn sách hàn lâm dành cho tất cả mọi người!', 'Tiếng Việt', 2021, 'NXB Thế Giới', 121000, 200, 0, 0.8, '1654690703823_Tu duy Nhanh va cham.jpg', '1654690703824_Tu duy Nhanh va cham.jpg', 0, '2022-06-08 19:18:23', '2022-06-08 19:37:10');
INSERT INTO `books` VALUES (31, 'Chủ Nghĩa Khắc Kỷ - Phong Cách Sống Bản Lĩnh Và Bình Thản', 'William B Irvine', NULL, 'Bạn mong muốn điều gì từ cuộc sống này? Có thể câu trả lời của bạn là muốn có một người bạn đời biết quan tâm, một công việc tốt và một ngôi nhà đẹp, nhưng chúng thực ra chỉ là một số thứ bạn muốn có trong cuộc sống. Khi hỏi bạn mong muốn điều gì từ cuộc sống này, tôi đang hỏi theo nghĩa rộng nhất. Tôi không hỏi về những mục tiêu mà bạn đề ra khi thực hiện các hoạt động hằng ngày, mà tôi đang hỏi về mục tiêu lớn lao trong cuộc sống của bạn. Nói cách khác, trong số những thứ bạn có thể theo đuổi trong cuộc sống, thứ nào bạn tin là có giá trị nhất?\r\n\r\nNhiều người sẽ khó lòng nêu ra được mục tiêu này. Họ biết mình muốn gì trong từng phút một hoặc thậm chí từng thập kỷ một trong suốt cuộc đời mình, nhưng họ chưa bao giờ dành thời gian để suy ngẫm về mục tiêu sống lớn lao của bản thân. Chuyện này có lẽ cũng dễ hiểu. Nền văn hóa của chúng ta vốn không khuyến khích mọi người nghĩ về những điều như vậy, mà chỉ tạo ra hết xao lãng này đến xao lãng khác, để chúng ta không bao giờ phải bận tâm đến chúng. Nhưng một mục tiêu lớn lao trong đời là thành phần đầu tiên của một triết lý sống. Nếu bạn không có một mục tiêu lớn lao trong đời, tức là bạn không có một triết lý sống chặt chẽ.\r\n\r\nNhưng tại sao có một triết lý sống lại quan trọng? Vì nếu không có nó, bạn sẽ có nguy cơ sống lầm lạc – bất kể bạn đã làm gì, bất kể mọi niềm vui thú mà bạn đã thụ hưởng lúc sinh thời, chung quy bạn vẫn sẽ sống một cuộc đời tồi tệ. Nói cách khác, có nguy cơ là lúc bạn đang nằm hấp hối trên giường, bạn sẽ nhìn lại và nhận ra rằng mình đã uổng phí một cơ hội sống. Thay vì dành cuộc đời mình để theo đuổi điều gì đó thực sự đáng giá, bạn đã phung phí nó khi mặc cho bản thân bị xao lãng trước vô số thứ phù phiếm mà cuộc đời đưa đến.\r\n\r\nGiờ giả sử bạn đã xác định được mục tiêu lớn lao trong đời mình. Và bạn cũng biết rõ tại sao mục tiêu này lại đáng để phấn đấu. Dù thế, bạn vẫn có nguy cơ sống lầm lạc. Bạn có thể sẽ không đạt được mục tiêu này, nhất là nếu không có một chiến lược hiệu quả. Do đó, thành phần thứ hai của một triết lý sống là một chiến lược để đạt được mục tiêu lớn lao của bạn. Chiến lược này sẽ chỉ rõ cho bạn phải làm những gì trong cuộc sống hằng ngày, từ đó tối đa hóa khả năng đạt được điều mà bạn xem là đáng giá nhất trong cuộc đời mình.\r\nCuốn sách này dành cho những người đang tìm kiếm một triết lý sống. Trong các trang tiếp theo, tôi sẽ tập trung vào một triết lý mà tôi thấy hữu ích và tôi nghĩ rằng nhiều độc giả cũng sẽ thấy như vậy. Đó là triết lý của trường phái Khắc kỷ cổ đại. Tuy triết lý sống này đã lâu đời nhưng ngày nay nó xứng đáng nhận được sự chú ý của bất kỳ cá nhân nào mong muốn có được một cuộc sống vừa ý nghĩa vừa trọn vẹn – những người mong muốn có được một cuộc sống tốt đẹp.\r\n\r\nNói cách khác, cuốn sách này đưa ra lời khuyên mọi người nên sống như thế nào. Đúng hơn, tôi sẽ là cầu nối mang đến cho bạn lời khuyên của các triết gia Khắc kỷ từ hai ngàn năm trước. Đây là điều mà các triết gia đồng nghiệp của tôi thường miễn cưỡng thực hiện, nhưng nói đi cũng phải nói lại, họ chủ yếu quan tâm đến “tính học thuật” của triết học; tức là họ chuyên nghiên cứu về lý thuyết hoặc lịch sử. Ngược lại, tôi quan tâm đến tính thực tiễn của chủ nghĩa Khắc kỷ: mục tiêu của tôi là áp dụng triết lý này vào cuộc sống của mình và khuyến khích người khác áp dụng nó vào cuộc sống của họ. Tôi cho rằng các triết gia Khắc kỷ cổ đại sẽ khuyến khích cả hai đường hướng này, nhưng họ cũng sẽ khẳng định rằng lý do chính để tìm hiểu về chủ nghĩa Khắc kỷ là nhằm áp dụng nó vào thực tiễn.\r\n\r\nĐiểm nữa cần hiểu rõ là mặc dù chủ nghĩa Khắc kỷ là một triết lý, nhưng nó cũng bao hàm cả yếu tố tâm lý. Các nhà Khắc kỷ nhận ra rằng một cuộc sống đầy rẫy cảm xúc tiêu cực – bao gồm tức giận, lo lắng, sợ hãi, đau buồn và ghen tị – không phải là một cuộc sống tốt đẹp. Do đó, họ trở thành những nhà quan sát nhạy bén về hoạt động của tâm trí con người và kết quả là trở thành một số nhà tâm lý học uyên bác nhất thời cổ đại. Họ tiếp tục phát triển các kỹ thuật để ngăn không cho các cảm xúc tiêu cực xuất hiện và để dập tắt chúng khi những nỗ lực ngăn chặn thất bại. Ngay cả những độc giả không tin tưởng phương pháp suy diễn của triết học cũng nên quan tâm đến các kỹ thuật này. Suy cho cùng, ai lại không muốn giảm bớt số lượng cảm xúc tiêu cực trong cuộc sống hằng ngày cơ chứ?\r\nMặc dù tôi đã nghiên cứu triết học trong suốt cả quãng đời trưởng thành, nhưng thực ra mãi cho đến thời gian gần đây, vốn kiến thức của tôi về chủ nghĩa Khắc kỷ vẫn còn nghèo nàn vô cùng. Các giảng viên đại học và cao học chưa từng yêu cầu tôi tìm đọc các triết gia Khắc kỷ, và mặc dù cũng ham đọc sách, nhưng tôi lại không có nhu cầu tìm đọc họ.\r\n\r\nNhìn chung, tôi không có nhu cầu chiêm nghiệm về một triết lý sống nào cả. Thay vào đó, như hầu hết mọi người, tôi cảm thấy thoải mái với thứ triết lý sống mặc định: theo đuổi tiền tài, địa vị xã hội và lạc thú. Có thể xem triết lý sống của tôi là một dạng chủ nghĩa khoái lạc khai ngộ.\r\n\r\nThế nhưng, ở độ tuổi ngoài 40, dòng đời bắt đầu đưa đẩy tôi tiếp xúc với chủ nghĩa Khắc kỷ. Đầu tiên phải kể đến cuốn tiểu thuyết A Man in Full của Tom Wolfe xuất bản năm 1998. Trong truyện, một nhân vật tình cờ phát hiện ra triết gia Khắc kỷ Epictetus và sau đó luôn nhiệt tình trích dẫn triết lý sống của ông. Tôi thấy điều này vừa hấp dẫn lại vừa khó hiểu.\r\n\r\nHai năm sau, tôi bắt đầu tiến hành nghiên cứu để viết một cuốn sách về chủ đề ham muốn. Trong quá trình này, tôi xem xét những lời khuyên đã được đưa ra suốt hàng thiên niên kỷ qua về việc làm chủ ham muốn. Tôi bắt đầu tìm hiểu quan điểm của các tôn giáo về ham muốn, bao gồm Cơ Đốc giáo, Ấn Độ giáo, Đạo giáo, Sufi giáo và Phật giáo (nhất là Thiền tông). Tôi tiếp tục xem xét lời khuyên về cách làm chủ ham muốn của các triết gia phương Tây nhưng nhận thấy chỉ có một số ít người đề cập đến vấn đề này, nổi bật là các triết gia Hy Lạp cổ đại thuộc trường phái Epicurean, trường phái Hoài nghi và trường phái Khắc kỷ.\r\n\r\nThực ra, tôi có một động cơ sâu xa khi nghiên cứu về chủ đề ham muốn. Từ lâu tôi đã mến mộ Thiền tông và nghĩ rằng nếu chiêm nghiệm kỹ hơn về nó trong quá trình nghiên cứu, tôi có thể trở thành một Thiền sư thực thụ. Thế nhưng, tôi bất ngờ phát hiện ra một số điểm tương đồng giữa chủ nghĩa Khắc kỷ và Thiền tông. Chẳng hạn, cả hai đều nhấn mạnh tầm quan trọng của việc suy ngẫm về bản chất tạm thời của thế giới xung quanh chúng ta và tầm quan trọng của việc làm chủ ham muốn, trong giới hạn khả năng cho phép. Họ cũng khuyên chúng ta theo đuổi sự bình thản và đưa ra chỉ dẫn để đạt đến và duy trì được trạng thái này. Ngoài ra, tôi cũng nhận thấy chủ nghĩa Khắc kỷ phù hợp với bản tính ưa phân tích mọi sự của tôi hơn là Phật giáo. Kết quả là tôi nghĩ đến chuyện trở thành một người thực hành chủ nghĩa Khắc kỷ, thay vì là một người tu tập Thiền tông.\r\n\r\nTrước khi bắt đầu nghiên cứu về ham muốn, đối với tôi, chủ nghĩa Khắc kỷ là một triết lý sống không thiết thực, nhưng khi đọc tác phẩm của các triết gia Khắc kỷ, hóa ra hầu hết mọi điều tôi từng biết về họ đều sai bét. Đầu tiên, tôi biết rằng từ điển định nghĩa một người Khắc kỷ là “người dường như lãnh đạm hoặc không bị ảnh hưởng bởi niềm vui, nỗi buồn, khoái lạc hay đau đớn”. Vì vậy, tôi tưởng họ là những người hay kìm nén cảm xúc. Thế nhưng, tôi khám phá ra rằng mục tiêu của chủ nghĩa Khắc kỷ không phải là loại trừ cảm xúc khỏi cuộc sống mà là loại trừ những cảm xúc tiêu cực.\r\n\r\nKhi đọc các tác phẩm của chủ nghĩa Khắc kỷ, tôi bắt gặp những cá nhân vui vẻ và lạc quan về cuộc sống (mặc dù họ luôn dành thời gian để suy nghĩ đến tất cả những viễn cảnh tồi tệ có thể xảy ra với họ), những cá nhân có khả năng tận hưởng trọn vẹn những thú vui của cuộc sống (đồng thời cũng thận trọng để không trở thành nô lệ cho những thú vui đó). Ngạc nhiên làm sao, tôi cũng bắt gặp những cá nhân coi trọng niềm vui; quả thực, theo Seneca, điều mà các nhà Khắc kỷ cố gắng khám phá là “cách tâm trí có thể luôn luôn theo đuổi một lộ trình kiên định và thuận lợi, có khuynh hướng tích cực đối với chính nó, và nhìn nhận được các trạng thái của nó bằng niềm vui”. Ông cũng khẳng định rằng người thực hành các nguyên tắc Khắc kỷ “cần phải luôn, dù anh ta có muốn hay không, cảm thấy hân hoan và có một niềm vui sâu sắc phát xuất từ bên trong, bởi lẽ anh ta thích thú với những nguồn vui của riêng mình, và không hề khao khát niềm vui nào lớn hơn những niềm vui nội tại này”. Tương tự, triết gia Khắc kỷ Musonius Rufus nói rằng nếu sống theo những nguyên tắc Khắc kỷ thì chúng ta tất có được “tâm tính vui vẻ và niềm vui bền vững”.\r\n\r\nThay vì sống thụ động, nhẫn nhục cam chịu sự bất công và bạo hành của thế giới, các nhà Khắc kỷ đã nỗ lực trọn đời để biến thế giới thành một nơi tốt đẹp hơn. Chẳng hạn như Cato Trẻ, triết lý sống Khắc kỷ của Cato không hề ngăn cản ông dũng cảm đấu tranh để khôi phục nền Cộng hòa La Mã. (Tuy không đóng góp tác phẩm nào cho chủ nghĩa Khắc kỷ, nhưng Cato là một nhà Khắc kỷ; kỳ thực, Seneca xem ông là một nhà Khắc kỷ hoàn hảo.) Cũng vậy, Seneca dường như luôn tràn đầy nhiệt huyết: không chỉ là một triết gia, ông còn là một nhà soạn kịch thành công, cố vấn chính trị cho hoàng đế, và có thể xem là một chủ ngân hàng đầu tư vào thời đó. Và Marcus Aurelius, ông không chỉ là một triết gia mà còn là một hoàng đế La Mã – kỳ thực, ông được xem là một trong những hoàng đế La Mã vĩ đại nhất. Khi đọc về các nhà Khắc kỷ, tôi cảm thấy vô cùng thán phục họ. Họ dũng cảm, chừng mực, lý trí và kỷ luật – những phẩm chất mà tôi muốn có. Họ cũng cho rằng điều quan trọng đối với chúng ta là làm trọn bổn phận của mình và giúp đỡ đồng loại – những giá trị mà tôi sẽ chia sẻ.\r\n\r\nTrong quá trình nghiên cứu về chủ đề ham muốn, tôi khám phá ra rằng những nhà tư tưởng đều nhất trí một điều, đó là nếu không vượt qua được thói tham lam vô độ, không biết thỏa mãn của mình, chúng ta sẽ khó lòng có được một cuộc sống tốt đẹp và ý nghĩa. Họ cũng nhất trí rằng cách thức hữu hiệu giúp chúng ta chế ngự xu hướng lúc nào cũng muốn có nhiều hơn này là thuyết phục bản thân muốn những thứ mình đã có sẵn. Đây dường như là một tri kiến quan trọng, nhưng nó đặt ra một câu hỏi rằng chính xác thì chúng ta cần phải làm gì để đạt được điều đó. Tôi vui mừng khi phát hiện ra rằng các nhà Khắc kỷ đã có lời giải cho câu hỏi này. Họ đã phát triển một kỹ thuật khá đơn giản, nếu được thực hành, có thể giúp chúng ta hân hoan với con người hiện tại của mình, dù chỉ trong một khoảng thời gian, sống cuộc đời mà chúng ta đang sống, bất kể cuộc đời đó có ra sao đi nữa.\r\n\r\nCàng nghiên cứu về chủ nghĩa Khắc kỷ, tôi càng bị cuốn vào triết lý của họ. Nhưng khi nổi hứng chia sẻ với người khác về chủ nghĩa Khắc kỷ, tôi sớm nhận ra rằng không chỉ có mỗi tôi hiểu sai về triết lý này. Bạn bè, người thân và ngay cả đồng nghiệp của tôi ở trường đại học dường như đều cho rằng các nhà Khắc kỷ là những người đặt mục tiêu kìm nén mọi cảm xúc và do đó sống một cuộc đời lãnh đạm và tẻ nhạt. Tôi chợt nhận ra rằng các nhà Khắc kỷ là nạn nhân của những định kiến sai lầm.\r\n\r\nChỉ riêng vấn đề này cũng đã đủ thôi thúc tôi viết một cuốn sách về các nhà Khắc kỷ – một cuốn sách sẽ đính chính lại sự thật – nhưng trong quá trình viết, tôi dần có một động lực thứ hai thậm chí còn mạnh mẽ hơn. Sau khi nghiên cứu chủ nghĩa Khắc kỷ, tôi bắt đầu thử sống theo triết lý này một cách có chừng mực. Đến nay, thử nghiệm đã thành công đến mức tôi nóng lòng muốn loan báo cho cả thế giới những gì mà tôi khám phá được, vì tôi tin rằng mọi người đều có thể thu được lợi ích từ việc nghiên cứu các nhà Khắc kỷ và áp dụng triết lý sống của họ.\r\n\r\nVào thời Hy Lạp và La Mã cổ đại, nếu ai đó muốn trở thành người Khắc kỷ, họ có thể gia nhập một trường học của phái Khắc kỷ. Nhưng điều này hiện không còn khả thi, nếu muốn trở thành người Khắc kỷ, bạn chỉ có thể tham khảo tác phẩm của các nhà Khắc kỷ thời cổ đại, nhưng bạn sẽ phát hiện ra rằng nhiều tác phẩm trong số đó đã bị thất lạc, nhất là tác phẩm của các nhà Khắc kỷ Hy Lạp. Hơn nữa, nếu đọc các tác phẩm còn sót lại, bạn sẽ thấy mặc dù chúng trình bày và phân tích chi tiết về chủ nghĩa Khắc kỷ nhưng lại không đưa ra một giáo án dành cho những người mới học về chủ nghĩa Khắc kỷ. Thách thức mà tôi đối mặt trong quá trình viết cuốn sách này là xây dựng một kế hoạch như vậy từ những manh mối rải rác trong các tác phẩm của chủ nghĩa Khắc kỷ.\r\nMặc dù phần còn lại của cuốn sách sẽ cung cấp các hướng dẫn chi tiết để thực hành chủ nghĩa Khắc kỷ, nhưng tôi xin phép mô tả sơ bộ ở đây một số điều cần phải làm nếu chúng ta lựa chọn chủ nghĩa Khắc kỷ làm triết lý sống.\r\n\r\nChúng ta sẽ xem xét lại các mục tiêu trong cuộc sống. Đặc biệt, chúng ta sẽ thuộc nằm lòng tuyên ngôn của phái Khắc kỷ, rằng nhiều thứ mà chúng ta khao khát vốn dĩ chẳng đáng để theo đuổi, nhất là danh vọng và của cải. Thay vào đó, chúng ta tập trung theo đuổi sự bình thản và cái mà các nhà Khắc kỷ gọi là đức hạnh. Chúng ta sẽ khám phá ra rằng đức hạnh của phái Khắc kỷ không giống với những gì con người thời nay nghĩ về từ này. Chúng ta cũng sẽ khám phá ra rằng sự bình thản mà các nhà Khắc kỷ truy cầu không phải là trạng thái bình thản có thể đạt được bằng cách uống thuốc an thần; nói cách khác, đó không phải là một trạng thái giống như thây ma. Thay vì vậy, nó là một trạng thái không có các cảm xúc tiêu cực, chẳng hạn như tức giận, đau buồn, lo âu và sợ hãi, mà chỉ có những cảm xúc tích cực – đặc biệt là niềm vui.\r\n\r\nChúng ta sẽ nghiên cứu các kỹ thuật tâm lý đã được các nhà Khắc kỷ phát triển nhằm đạt được và duy trì sự bình thản, đồng thời sử dụng các kỹ thuật này vào cuộc sống hằng ngày. Ví dụ, chúng ta sẽ cẩn thận phân biệt giữa những thứ mình có thể kiểm soát và không thể kiểm soát, nhờ đó không còn bận tâm đến những thứ không thể kiểm soát và tập trung vào những thứ có thể kiểm soát. Chúng ta cũng sẽ nhận ra rằng người khác dễ dàng làm xáo trộn sự bình thản của chúng ta như thế nào, và do đó sẽ thực hành các chiến lược của phái Khắc kỷ để ngăn không cho họ quấy nhiễu chúng ta.\r\nCuối cùng, chúng ta sẽ quan sát chính cuộc sống của mình một cách sâu sắc hơn. Chúng ta sẽ dõi theo bản thân khi thực hiện các công việc hằng ngày, sau đó suy ngẫm về những\r\nđiều chúng ta thấy, cố gắng xác định những nguồn cơn gây ra đau khổ trong đời mình và tìm cách ngăn ngừa nỗi đau khổ đó.\r\n\r\nThực hành chủ nghĩa Khắc kỷ rõ ràng sẽ đòi hỏi sự nỗ lực, nhưng điều này đúng với tất cả các triết lý sống chân chính. Thật thế, ngay cả chủ nghĩa “khoái lạc khai ngộ” cũng đòi hỏi sự nỗ lực. Mục tiêu lớn lao trong cuộc sống của những người theo chủ nghĩa khoái lạc khai ngộ là tối đa hóa những lạc thú mà anh ta trải nghiệm trong suốt cuộc đời. Để thực hành triết lý sống này, anh ta sẽ phải dành thời gian tìm tòi, khám phá, xếp hạng các loại lạc thú và rà soát mọi tác dụng phụ không mong muốn có thể phát sinh. Sau đó, người theo chủ nghĩa khoái lạc khai ngộ sẽ đưa ra các chiến lược nhằm tối đa hóa những lạc thú mà anh ta trải nghiệm. (Chủ nghĩa khoái lạc mê muội, mà ở đó người thực hành tìm kiếm sự thỏa mãn ngắn hạn một cách thiếu cân nhắc, theo tôi là một triết lý sống không nhất quán.)\r\n\r\nThực hành chủ nghĩa Khắc kỷ có lẽ sẽ đòi hỏi sự nỗ lực lớn hơn so với thực hành chủ nghĩa khoái lạc khai ngộ, nhưng lại dễ dàng hơn so với thực hành Thiền Tông. Một Phật tử Thiền tông sẽ phải thiền, một sự tu tập vừa mất nhiều thời gian vừa khó về mặt thể chất và tinh thần. Ngược lại, thực hành chủ nghĩa Khắc kỷ không đòi hỏi chúng ta dành riêng một khoảng thời gian để “tập Khắc kỷ”. Mặc dù chúng ta cần phải định kỳ suy ngẫm về cuộc đời mình, nhưng nhìn chung là khoảng thời gian suy ngẫm này có thể chèn vào những thời điểm rảnh rỗi trong ngày, chẳng hạn như khi chúng ta bị kẹt xe hoặc – theo lời khuyên của Seneca – khi chúng ta đang nằm trên giường và chuẩn bị ngủ.\r\nKhi đánh giá các “chi phí” gắn liền với việc thực hành chủ nghĩa Khắc kỷ hoặc bất kỳ triết lý sống nào khác, độc giả cần phải nhận thức rằng việc không có một triết lý sống cũng khiến bạn phải trả giá. Tôi đã chỉ ra một cái giá phải trả: nguy cơ bạn sẽ dành những tháng ngày cuộc đời theo đuổi những thứ không có giá trị và dẫn đến lãng phí cuộc đời mình.\r\n\r\nCó thể lúc này, một số độc giả sẽ tự hỏi liệu việc thực hành chủ nghĩa Khắc kỷ có phù hợp với đức tin tôn giáo của họ hay không. Đối với phần lớn các tôn giáo, tôi nghĩ là có. Những người theo đạo Cơ Đốc nói riêng sẽ thấy những giáo lý của chủ nghĩa Khắc kỷ cộng hưởng với các quan điểm tôn giáo của họ. Chẳng hạn, họ cũng mong muốn đạt được sự bình thản giống như các nhà Khắc kỷ, mặc dù những người theo đạo Cơ Đốc có thể gọi nó là sự bình an. Họ sẽ đánh giá cao lời huấn thị “yêu thương nhân loại” của Marcus Aurelius. Và khi những người theo đạo Cơ Đốc bắt gặp luận điểm của Epictetus rằng có những thứ nằm trong tầm kiểm soát và có những thứ không nằm trong tầm kiểm soát của chúng ta, và rằng nếu có nhận thức, chúng ta sẽ tập trung năng lượng vào những thứ nằm trong tầm kiểm soát, họ sẽ được gợi nhắc về Lời cầu nguyện Bình an vốn thường được cho là sáng tác của nhà thần học Reinhold Niebuhr.\r\n\r\nTôi cần phải nói thêm rằng ngay cả những người theo thuyết bất khả tri cũng hoàn toàn có thể đồng thời thực hành chủ nghĩa Khắc kỷ Phần còn lại của cuốn sách này được chia làm bốn phần. Trong phần 1, tôi trình bày sự hình thành của triết học. Mặc dù các triết gia hiện đại thường dành cả đời để tranh luận về các chủ đề trừu tượng, nhưng mục tiêu chính của hầu hết các triết gia cổ đại là giúp người bình thường sống tốt hơn. Như chúng ta sẽ thấy, chủ nghĩa Khắc kỷ là một trong những trường phái triết học cổ đại phổ biến và thành công nhất.\r\n\r\nTrong phần 2 và 3, tôi giải thích những gì chúng ta cần phải làm để thực hành chủ nghĩa Khắc kỷ. Mở đầu là các kỹ thuật tâm lý đã được chủ nghĩa Khắc kỷ phát triển để đạt đến và sau đó là duy trì sự bình thản. Kế tiếp, tôi sẽ đưa ra lời khuyên của phái Khắc kỷ về cách đối phó hiệu quả nhất với những căng thẳng trong cuộc sống thường ngày: Chẳng hạn, chúng ta nên phản ứng như thế nào khi bị người khác xúc phạm? Mặc dù nhiều thứ đã thay đổi trong hai thiên niên kỷ qua, nhưng tâm lý con người thì không mấy thay đổi. Do đó, những người đang sống ở thế kỷ hai mốt có thể gặt hái được lợi ích từ lời khuyên vốn được các triết gia như Seneca đưa ra cho người La Mã ở thế kỷ thứ nhất.\r\nCuối cùng, trong phần 4 của cuốn sách, tôi bảo vệ chủ nghĩa Khắc kỷ trước những lời chỉ trích, cũng như đánh giá lại tâm lý học Khắc kỷ dưới ánh sáng của các phát hiện khoa học hiện đại. Tôi kết thúc cuốn sách bằng cách thuật lại những nhận thức mà mình đã đạt được trong quá trình thực hành chủ nghĩa Khắc kỷ.\r\n\r\nCác học giả đồng nghiệp của tôi có thể hứng thú với cuốn sách này; chẳng hạn họ tò mò xem tôi diễn giải những phát biểu của chủ nghĩa Khắc kỷ như thế nào. Tuy nhiên, đối tượng độc giả chính mà tôi hướng đến là những cá nhân bình thường, những người băn khoăn không biết bản thân có đang sống lầm lỗi hay không. Đối tượng này gồm những người dần nhận ra rằng họ thiếu một triết lý sống nhất quán và hậu quả là họ đang lúng túng trong các hoạt động thường ngày của mình: những thành quả họ đạt được ngày hôm nay lại phủi sạch những thành quả đã đạt được ngày hôm trước. Đồng thời, tôi cũng hướng đến những người đã có một triết lý sống nhưng lo ngại rằng triết lý đó phần nào khiếm khuyết.\r\n\r\nTrong quá trình viết cuốn sách này, tôi luôn đặt ra trong đầu câu hỏi: Nếu các nhà Khắc kỷ cổ đại đảm nhận trọng trách viết một cuốn sách hướng dẫn cho những người sống ở thế kỷ hai mốt – một cuốn sách chỉ cho chúng ta cách có được một cuộc sống tốt đẹp – thì cuốn sách đó sẽ như thế nào. Các trang tiếp theo là câu trả lời của tôi cho câu hỏi này.” Chia sẻ của tác giả William B.Irvine tới bạn đọc!\r\n\r\nMã hàng	8935280911109\r\nDự Kiến Có Hàng	18/06/2022\r\nTên Nhà Cung Cấp	Thái Hà\r\nTác giả	William B Irvine\r\nNgười Dịch	Nhóm Tâm Lý Học Tội Phạm\r\nNXB	NXB Công Thương\r\nNăm XB	2022\r\nTrọng lượng (gr)	450\r\nKích Thước Bao Bì	20.5 x 14.5 x 3 cm\r\nSố trang	380\r\nHình thức	Bìa Cứng\r\nSản phẩm hiển thị trong	\r\nThái Hà\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Kỹ năng sống bán chạy của tháng\r\nChủ Nghĩa Khắc Kỷ - Phong Cách Sống Bản Lĩnh Và Bình Thản\r\n\r\nBạn mong muốn điều gì từ cuộc sống này? Có thể câu trả lời của bạn là muốn có một người bạn đời biết quan tâm, một công việc tốt và một ngôi nhà đẹp, nhưng chúng thực ra chỉ là một số thứ bạn muốn có trong cuộc sống. Khi hỏi bạn mong muốn điều gì từ cuộc sống này, tôi đang hỏi theo nghĩa rộng nhất. Tôi không hỏi về những mục tiêu mà bạn đề ra khi thực hiện các hoạt động hằng ngày, mà tôi đang hỏi về mục tiêu lớn lao trong cuộc sống của bạn. Nói cách khác, trong số những thứ bạn có thể theo đuổi trong cuộc sống, thứ nào bạn tin là có giá trị nhất?\r\n\r\nNhiều người sẽ khó lòng nêu ra được mục tiêu này. Họ biết mình muốn gì trong từng phút một hoặc thậm chí từng thập kỷ một trong suốt cuộc đời mình, nhưng họ chưa bao giờ dành thời gian để suy ngẫm về mục tiêu sống lớn lao của bản thân. Chuyện này có lẽ cũng dễ hiểu. Nền văn hóa của chúng ta vốn không khuyến khích mọi người nghĩ về những điều như vậy, mà chỉ tạo ra hết xao lãng này đến xao lãng khác, để chúng ta không bao giờ phải bận tâm đến chúng. Nhưng một mục tiêu lớn lao trong đời là thành phần đầu tiên của một triết lý sống. Nếu bạn không có một mục tiêu lớn lao trong đời, tức là bạn không có một triết lý sống chặt chẽ.\r\n\r\nNhưng tại sao có một triết lý sống lại quan trọng? Vì nếu không có nó, bạn sẽ có nguy cơ sống lầm lạc – bất kể bạn đã làm gì, bất kể mọi niềm vui thú mà bạn đã thụ hưởng lúc sinh thời, chung quy bạn vẫn sẽ sống một cuộc đời tồi tệ. Nói cách khác, có nguy cơ là lúc bạn đang nằm hấp hối trên giường, bạn sẽ nhìn lại và nhận ra rằng mình đã uổng phí một cơ hội sống. Thay vì dành cuộc đời mình để theo đuổi điều gì đó thực sự đáng giá, bạn đã phung phí nó khi mặc cho bản thân bị xao lãng trước vô số thứ phù phiếm mà cuộc đời đưa đến.\r\n\r\nGiờ giả sử bạn đã xác định được mục tiêu lớn lao trong đời mình. Và bạn cũng biết rõ tại sao mục tiêu này lại đáng để phấn đấu. Dù thế, bạn vẫn có nguy cơ sống lầm lạc. Bạn có thể sẽ không đạt được mục tiêu này, nhất là nếu không có một chiến lược hiệu quả. Do đó, thành phần thứ hai của một triết lý sống là một chiến lược để đạt được mục tiêu lớn lao của bạn. Chiến lược này sẽ chỉ rõ cho bạn phải làm những gì trong cuộc sống hằng ngày, từ đó tối đa hóa khả năng đạt được điều mà bạn xem là đáng giá nhất trong cuộc đời mình.\r\nCuốn sách này dành cho những người đang tìm kiếm một triết lý sống. Trong các trang tiếp theo, tôi sẽ tập trung vào một triết lý mà tôi thấy hữu ích và tôi nghĩ rằng nhiều độc giả cũng sẽ thấy như vậy. Đó là triết lý của trường phái Khắc kỷ cổ đại. Tuy triết lý sống này đã lâu đời nhưng ngày nay nó xứng đáng nhận được sự chú ý của bất kỳ cá nhân nào mong muốn có được một cuộc sống vừa ý nghĩa vừa trọn vẹn – những người mong muốn có được một cuộc sống tốt đẹp.\r\n\r\nNói cách khác, cuốn sách này đưa ra lời khuyên mọi người nên sống như thế nào. Đúng hơn, tôi sẽ là cầu nối mang đến cho bạn lời khuyên của các triết gia Khắc kỷ từ hai ngàn năm trước. Đây là điều mà các triết gia đồng nghiệp của tôi thường miễn cưỡng thực hiện, nhưng nói đi cũng phải nói lại, họ chủ yếu quan tâm đến “tính học thuật” của triết học; tức là họ chuyên nghiên cứu về lý thuyết hoặc lịch sử. Ngược lại, tôi quan tâm đến tính thực tiễn của chủ nghĩa Khắc kỷ: mục tiêu của tôi là áp dụng triết lý này vào cuộc sống của mình và khuyến khích người khác áp dụng nó vào cuộc sống của họ. Tôi cho rằng các triết gia Khắc kỷ cổ đại sẽ khuyến khích cả hai đường hướng này, nhưng họ cũng sẽ khẳng định rằng lý do chính để tìm hiểu về chủ nghĩa Khắc kỷ là nhằm áp dụng nó vào thực tiễn.\r\n\r\nĐiểm nữa cần hiểu rõ là mặc dù chủ nghĩa Khắc kỷ là một triết lý, nhưng nó cũng bao hàm cả yếu tố tâm lý. Các nhà Khắc kỷ nhận ra rằng một cuộc sống đầy rẫy cảm xúc tiêu cực – bao gồm tức giận, lo lắng, sợ hãi, đau buồn và ghen tị – không phải là một cuộc sống tốt đẹp. Do đó, họ trở thành những nhà quan sát nhạy bén về hoạt động của tâm trí con người và kết quả là trở thành một số nhà tâm lý học uyên bác nhất thời cổ đại. Họ tiếp tục phát triển các kỹ thuật để ngăn không cho các cảm xúc tiêu cực xuất hiện và để dập tắt chúng khi những nỗ lực ngăn chặn thất bại. Ngay cả những độc giả không tin tưởng phương pháp suy diễn của triết học cũng nên quan tâm đến các kỹ thuật này. Suy cho cùng, ai lại không muốn giảm bớt số lượng cảm xúc tiêu cực trong cuộc sống hằng ngày cơ chứ?\r\nMặc dù tôi đã nghiên cứu triết học trong suốt cả quãng đời trưởng thành, nhưng thực ra mãi cho đến thời gian gần đây, vốn kiến thức của tôi về chủ nghĩa Khắc kỷ vẫn còn nghèo nàn vô cùng. Các giảng viên đại học và cao học chưa từng yêu cầu tôi tìm đọc các triết gia Khắc kỷ, và mặc dù cũng ham đọc sách, nhưng tôi lại không có nhu cầu tìm đọc họ.\r\n\r\nNhìn chung, tôi không có nhu cầu chiêm nghiệm về một triết lý sống nào cả. Thay vào đó, như hầu hết mọi người, tôi cảm thấy thoải mái với thứ triết lý sống mặc định: theo đuổi tiền tài, địa vị xã hội và lạc thú. Có thể xem triết lý sống của tôi là một dạng chủ nghĩa khoái lạc khai ngộ.\r\n\r\nThế nhưng, ở độ tuổi ngoài 40, dòng đời bắt đầu đưa đẩy tôi tiếp xúc với chủ nghĩa Khắc kỷ. Đầu tiên phải kể đến cuốn tiểu thuyết A Man in Full của Tom Wolfe xuất bản năm 1998. Trong truyện, một nhân vật tình cờ phát hiện ra triết gia Khắc kỷ Epictetus và sau đó luôn nhiệt tình trích dẫn triết lý sống của ông. Tôi thấy điều này vừa hấp dẫn lại vừa khó hiểu.\r\n\r\nHai năm sau, tôi bắt đầu tiến hành nghiên cứu để viết một cuốn sách về chủ đề ham muốn. Trong quá trình này, tôi xem xét những lời khuyên đã được đưa ra suốt hàng thiên niên kỷ qua về việc làm chủ ham muốn. Tôi bắt đầu tìm hiểu quan điểm của các tôn giáo về ham muốn, bao gồm Cơ Đốc giáo, Ấn Độ giáo, Đạo giáo, Sufi giáo và Phật giáo (nhất là Thiền tông). Tôi tiếp tục xem xét lời khuyên về cách làm chủ ham muốn của các triết gia phương Tây nhưng nhận thấy chỉ có một số ít người đề cập đến vấn đề này, nổi bật là các triết gia Hy Lạp cổ đại thuộc trường phái Epicurean, trường phái Hoài nghi và trường phái Khắc kỷ.\r\n\r\nThực ra, tôi có một động cơ sâu xa khi nghiên cứu về chủ đề ham muốn. Từ lâu tôi đã mến mộ Thiền tông và nghĩ rằng nếu chiêm nghiệm kỹ hơn về nó trong quá trình nghiên cứu, tôi có thể trở thành một Thiền sư thực thụ. Thế nhưng, tôi bất ngờ phát hiện ra một số điểm tương đồng giữa chủ nghĩa Khắc kỷ và Thiền tông. Chẳng hạn, cả hai đều nhấn mạnh tầm quan trọng của việc suy ngẫm về bản chất tạm thời của thế giới xung quanh chúng ta và tầm quan trọng của việc làm chủ ham muốn, trong giới hạn khả năng cho phép. Họ cũng khuyên chúng ta theo đuổi sự bình thản và đưa ra chỉ dẫn để đạt đến và duy trì được trạng thái này. Ngoài ra, tôi cũng nhận thấy chủ nghĩa Khắc kỷ phù hợp với bản tính ưa phân tích mọi sự của tôi hơn là Phật giáo. Kết quả là tôi nghĩ đến chuyện trở thành một người thực hành chủ nghĩa Khắc kỷ, thay vì là một người tu tập Thiền tông.\r\n\r\nTrước khi bắt đầu nghiên cứu về ham muốn, đối với tôi, chủ nghĩa Khắc kỷ là một triết lý sống không thiết thực, nhưng khi đọc tác phẩm của các triết gia Khắc kỷ, hóa ra hầu hết mọi điều tôi từng biết về họ đều sai bét. Đầu tiên, tôi biết rằng từ điển định nghĩa một người Khắc kỷ là “người dường như lãnh đạm hoặc không bị ảnh hưởng bởi niềm vui, nỗi buồn, khoái lạc hay đau đớn”. Vì vậy, tôi tưởng họ là những người hay kìm nén cảm xúc. Thế nhưng, tôi khám phá ra rằng mục tiêu của chủ nghĩa Khắc kỷ không phải là loại trừ cảm xúc khỏi cuộc sống mà là loại trừ những cảm xúc tiêu cực.\r\n\r\nKhi đọc các tác phẩm của chủ nghĩa Khắc kỷ, tôi bắt gặp những cá nhân vui vẻ và lạc quan về cuộc sống (mặc dù họ luôn dành thời gian để suy nghĩ đến tất cả những viễn cảnh tồi tệ có thể xảy ra với họ), những cá nhân có khả năng tận hưởng trọn vẹn những thú vui của cuộc sống (đồng thời cũng thận trọng để không trở thành nô lệ cho những thú vui đó). Ngạc nhiên làm sao, tôi cũng bắt gặp những cá nhân coi trọng niềm vui; quả thực, theo Seneca, điều mà các nhà Khắc kỷ cố gắng khám phá là “cách tâm trí có thể luôn luôn theo đuổi một lộ trình kiên định và thuận lợi, có khuynh hướng tích cực đối với chính nó, và nhìn nhận được các trạng thái của nó bằng niềm vui”. Ông cũng khẳng định rằng người thực hành các nguyên tắc Khắc kỷ “cần phải luôn, dù anh ta có muốn hay không, cảm thấy hân hoan và có một niềm vui sâu sắc phát xuất từ bên trong, bởi lẽ anh ta thích thú với những nguồn vui của riêng mình, và không hề khao khát niềm vui nào lớn hơn những niềm vui nội tại này”. Tương tự, triết gia Khắc kỷ Musonius Rufus nói rằng nếu sống theo những nguyên tắc Khắc kỷ thì chúng ta tất có được “tâm tính vui vẻ và niềm vui bền vững”.\r\n\r\nThay vì sống thụ động, nhẫn nhục cam chịu sự bất công và bạo hành của thế giới, các nhà Khắc kỷ đã nỗ lực trọn đời để biến thế giới thành một nơi tốt đẹp hơn. Chẳng hạn như Cato Trẻ, triết lý sống Khắc kỷ của Cato không hề ngăn cản ông dũng cảm đấu tranh để khôi phục nền Cộng hòa La Mã. (Tuy không đóng góp tác phẩm nào cho chủ nghĩa Khắc kỷ, nhưng Cato là một nhà Khắc kỷ; kỳ thực, Seneca xem ông là một nhà Khắc kỷ hoàn hảo.) Cũng vậy, Seneca dường như luôn tràn đầy nhiệt huyết: không chỉ là một triết gia, ông còn là một nhà soạn kịch thành công, cố vấn chính trị cho hoàng đế, và có thể xem là một chủ ngân hàng đầu tư vào thời đó. Và Marcus Aurelius, ông không chỉ là một triết gia mà còn là một hoàng đế La Mã – kỳ thực, ông được xem là một trong những hoàng đế La Mã vĩ đại nhất. Khi đọc về các nhà Khắc kỷ, tôi cảm thấy vô cùng thán phục họ. Họ dũng cảm, chừng mực, lý trí và kỷ luật – những phẩm chất mà tôi muốn có. Họ cũng cho rằng điều quan trọng đối với chúng ta là làm trọn bổn phận của mình và giúp đỡ đồng loại – những giá trị mà tôi sẽ chia sẻ.\r\n\r\nTrong quá trình nghiên cứu về chủ đề ham muốn, tôi khám phá ra rằng những nhà tư tưởng đều nhất trí một điều, đó là nếu không vượt qua được thói tham lam vô độ, không biết thỏa mãn của mình, chúng ta sẽ khó lòng có được một cuộc sống tốt đẹp và ý nghĩa. Họ cũng nhất trí rằng cách thức hữu hiệu giúp chúng ta chế ngự xu hướng lúc nào cũng muốn có nhiều hơn này là thuyết phục bản thân muốn những thứ mình đã có sẵn. Đây dường như là một tri kiến quan trọng, nhưng nó đặt ra một câu hỏi rằng chính xác thì chúng ta cần phải làm gì để đạt được điều đó. Tôi vui mừng khi phát hiện ra rằng các nhà Khắc kỷ đã có lời giải cho câu hỏi này. Họ đã phát triển một kỹ thuật khá đơn giản, nếu được thực hành, có thể giúp chúng ta hân hoan với con người hiện tại của mình, dù chỉ trong một khoảng thời gian, sống cuộc đời mà chúng ta đang sống, bất kể cuộc đời đó có ra sao đi nữa.\r\n\r\nCàng nghiên cứu về chủ nghĩa Khắc kỷ, tôi càng bị cuốn vào triết lý của họ. Nhưng khi nổi hứng chia sẻ với người khác về chủ nghĩa Khắc kỷ, tôi sớm nhận ra rằng không chỉ có mỗi tôi hiểu sai về triết lý này. Bạn bè, người thân và ngay cả đồng nghiệp của tôi ở trường đại học dường như đều cho rằng các nhà Khắc kỷ là những người đặt mục tiêu kìm nén mọi cảm xúc và do đó sống một cuộc đời lãnh đạm và tẻ nhạt. Tôi chợt nhận ra rằng các nhà Khắc kỷ là nạn nhân của những định kiến sai lầm.\r\n\r\nChỉ riêng vấn đề này cũng đã đủ thôi thúc tôi viết một cuốn sách về các nhà Khắc kỷ – một cuốn sách sẽ đính chính lại sự thật – nhưng trong quá trình viết, tôi dần có một động lực thứ hai thậm chí còn mạnh mẽ hơn. Sau khi nghiên cứu chủ nghĩa Khắc kỷ, tôi bắt đầu thử sống theo triết lý này một cách có chừng mực. Đến nay, thử nghiệm đã thành công đến mức tôi nóng lòng muốn loan báo cho cả thế giới những gì mà tôi khám phá được, vì tôi tin rằng mọi người đều có thể thu được lợi ích từ việc nghiên cứu các nhà Khắc kỷ và áp dụng triết lý sống của họ.\r\n\r\nVào thời Hy Lạp và La Mã cổ đại, nếu ai đó muốn trở thành người Khắc kỷ, họ có thể gia nhập một trường học của phái Khắc kỷ. Nhưng điều này hiện không còn khả thi, nếu muốn trở thành người Khắc kỷ, bạn chỉ có thể tham khảo tác phẩm của các nhà Khắc kỷ thời cổ đại, nhưng bạn sẽ phát hiện ra rằng nhiều tác phẩm trong số đó đã bị thất lạc, nhất là tác phẩm của các nhà Khắc kỷ Hy Lạp. Hơn nữa, nếu đọc các tác phẩm còn sót lại, bạn sẽ thấy mặc dù chúng trình bày và phân tích chi tiết về chủ nghĩa Khắc kỷ nhưng lại không đưa ra một giáo án dành cho những người mới học về chủ nghĩa Khắc kỷ. Thách thức mà tôi đối mặt trong quá trình viết cuốn sách này là xây dựng một kế hoạch như vậy từ những manh mối rải rác trong các tác phẩm của chủ nghĩa Khắc kỷ.\r\nMặc dù phần còn lại của cuốn sách sẽ cung cấp các hướng dẫn chi tiết để thực hành chủ nghĩa Khắc kỷ, nhưng tôi xin phép mô tả sơ bộ ở đây một số điều cần phải làm nếu chúng ta lựa chọn chủ nghĩa Khắc kỷ làm triết lý sống.\r\n\r\nChúng ta sẽ xem xét lại các mục tiêu trong cuộc sống. Đặc biệt, chúng ta sẽ thuộc nằm lòng tuyên ngôn của phái Khắc kỷ, rằng nhiều thứ mà chúng ta khao khát vốn dĩ chẳng đáng để theo đuổi, nhất là danh vọng và của cải. Thay vào đó, chúng ta tập trung theo đuổi sự bình thản và cái mà các nhà Khắc kỷ gọi là đức hạnh. Chúng ta sẽ khám phá ra rằng đức hạnh của phái Khắc kỷ không giống với những gì con người thời nay nghĩ về từ này. Chúng ta cũng sẽ khám phá ra rằng sự bình thản mà các nhà Khắc kỷ truy cầu không phải là trạng thái bình thản có thể đạt được bằng cách uống thuốc an thần; nói cách khác, đó không phải là một trạng thái giống như thây ma. Thay vì vậy, nó là một trạng thái không có các cảm xúc tiêu cực, chẳng hạn như tức giận, đau buồn, lo âu và sợ hãi, mà chỉ có những cảm xúc tích cực – đặc biệt là niềm vui.\r\n\r\nChúng ta sẽ nghiên cứu các kỹ thuật tâm lý đã được các nhà Khắc kỷ phát triển nhằm đạt được và duy trì sự bình thản, đồng thời sử dụng các kỹ thuật này vào cuộc sống hằng ngày. Ví dụ, chúng ta sẽ cẩn thận phân biệt giữa những thứ mình có thể kiểm soát và không thể kiểm soát, nhờ đó không còn bận tâm đến những thứ không thể kiểm soát và tập trung vào những thứ có thể kiểm soát. Chúng ta cũng sẽ nhận ra rằng người khác dễ dàng làm xáo trộn sự bình thản của chúng ta như thế nào, và do đó sẽ thực hành các chiến lược của phái Khắc kỷ để ngăn không cho họ quấy nhiễu chúng ta.\r\nCuối cùng, chúng ta sẽ quan sát chính cuộc sống của mình một cách sâu sắc hơn. Chúng ta sẽ dõi theo bản thân khi thực hiện các công việc hằng ngày, sau đó suy ngẫm về những\r\nđiều chúng ta thấy, cố gắng xác định những nguồn cơn gây ra đau khổ trong đời mình và tìm cách ngăn ngừa nỗi đau khổ đó.\r\n\r\nThực hành chủ nghĩa Khắc kỷ rõ ràng sẽ đòi hỏi sự nỗ lực, nhưng điều này đúng với tất cả các triết lý sống chân chính. Thật thế, ngay cả chủ nghĩa “khoái lạc khai ngộ” cũng đòi hỏi sự nỗ lực. Mục tiêu lớn lao trong cuộc sống của những người theo chủ nghĩa khoái lạc khai ngộ là tối đa hóa những lạc thú mà anh ta trải nghiệm trong suốt cuộc đời. Để thực hành triết lý sống này, anh ta sẽ phải dành thời gian tìm tòi, khám phá, xếp hạng các loại lạc thú và rà soát mọi tác dụng phụ không mong muốn có thể phát sinh. Sau đó, người theo chủ nghĩa khoái lạc khai ngộ sẽ đưa ra các chiến lược nhằm tối đa hóa những lạc thú mà anh ta trải nghiệm. (Chủ nghĩa khoái lạc mê muội, mà ở đó người thực hành tìm kiếm sự thỏa mãn ngắn hạn một cách thiếu cân nhắc, theo tôi là một triết lý sống không nhất quán.)\r\n\r\nThực hành chủ nghĩa Khắc kỷ có lẽ sẽ đòi hỏi sự nỗ lực lớn hơn so với thực hành chủ nghĩa khoái lạc khai ngộ, nhưng lại dễ dàng hơn so với thực hành Thiền Tông. Một Phật tử Thiền tông sẽ phải thiền, một sự tu tập vừa mất nhiều thời gian vừa khó về mặt thể chất và tinh thần. Ngược lại, thực hành chủ nghĩa Khắc kỷ không đòi hỏi chúng ta dành riêng một khoảng thời gian để “tập Khắc kỷ”. Mặc dù chúng ta cần phải định kỳ suy ngẫm về cuộc đời mình, nhưng nhìn chung là khoảng thời gian suy ngẫm này có thể chèn vào những thời điểm rảnh rỗi trong ngày, chẳng hạn như khi chúng ta bị kẹt xe hoặc – theo lời khuyên của Seneca – khi chúng ta đang nằm trên giường và chuẩn bị ngủ.\r\nKhi đánh giá các “chi phí” gắn liền với việc thực hành chủ nghĩa Khắc kỷ hoặc bất kỳ triết lý sống nào khác, độc giả cần phải nhận thức rằng việc không có một triết lý sống cũng khiến bạn phải trả giá. Tôi đã chỉ ra một cái giá phải trả: nguy cơ bạn sẽ dành những tháng ngày cuộc đời theo đuổi những thứ không có giá trị và dẫn đến lãng phí cuộc đời mình.\r\n\r\nCó thể lúc này, một số độc giả sẽ tự hỏi liệu việc thực hành chủ nghĩa Khắc kỷ có phù hợp với đức tin tôn giáo của họ hay không. Đối với phần lớn các tôn giáo, tôi nghĩ là có. Những người theo đạo Cơ Đốc nói riêng sẽ thấy những giáo lý của chủ nghĩa Khắc kỷ cộng hưởng với các quan điểm tôn giáo của họ. Chẳng hạn, họ cũng mong muốn đạt được sự bình thản giống như các nhà Khắc kỷ, mặc dù những người theo đạo Cơ Đốc có thể gọi nó là sự bình an. Họ sẽ đánh giá cao lời huấn thị “yêu thương nhân loại” của Marcus Aurelius. Và khi những người theo đạo Cơ Đốc bắt gặp luận điểm của Epictetus rằng có những thứ nằm trong tầm kiểm soát và có những thứ không nằm trong tầm kiểm soát của chúng ta, và rằng nếu có nhận thức, chúng ta sẽ tập trung năng lượng vào những thứ nằm trong tầm kiểm soát, họ sẽ được gợi nhắc về Lời cầu nguyện Bình an vốn thường được cho là sáng tác của nhà thần học Reinhold Niebuhr.\r\n\r\nTôi cần phải nói thêm rằng ngay cả những người theo thuyết bất khả tri cũng hoàn toàn có thể đồng thời thực hành chủ nghĩa Khắc kỷ Phần còn lại của cuốn sách này được chia làm bốn phần. Trong phần 1, tôi trình bày sự hình thành của triết học. Mặc dù các triết gia hiện đại thường dành cả đời để tranh luận về các chủ đề trừu tượng, nhưng mục tiêu chính của hầu hết các triết gia cổ đại là giúp người bình thường sống tốt hơn. Như chúng ta sẽ thấy, chủ nghĩa Khắc kỷ là một trong những trường phái triết học cổ đại phổ biến và thành công nhất.\r\n\r\nTrong phần 2 và 3, tôi giải thích những gì chúng ta cần phải làm để thực hành chủ nghĩa Khắc kỷ. Mở đầu là các kỹ thuật tâm lý đã được chủ nghĩa Khắc kỷ phát triển để đạt đến và sau đó là duy trì sự bình thản. Kế tiếp, tôi sẽ đưa ra lời khuyên của phái Khắc kỷ về cách đối phó hiệu quả nhất với những căng thẳng trong cuộc sống thường ngày: Chẳng hạn, chúng ta nên phản ứng như thế nào khi bị người khác xúc phạm? Mặc dù nhiều thứ đã thay đổi trong hai thiên niên kỷ qua, nhưng tâm lý con người thì không mấy thay đổi. Do đó, những người đang sống ở thế kỷ hai mốt có thể gặt hái được lợi ích từ lời khuyên vốn được các triết gia như Seneca đưa ra cho người La Mã ở thế kỷ thứ nhất.\r\nCuối cùng, trong phần 4 của cuốn sách, tôi bảo vệ chủ nghĩa Khắc kỷ trước những lời chỉ trích, cũng như đánh giá lại tâm lý học Khắc kỷ dưới ánh sáng của các phát hiện khoa học hiện đại. Tôi kết thúc cuốn sách bằng cách thuật lại những nhận thức mà mình đã đạt được trong quá trình thực hành chủ nghĩa Khắc kỷ.\r\n\r\nCác học giả đồng nghiệp của tôi có thể hứng thú với cuốn sách này; chẳng hạn họ tò mò xem tôi diễn giải những phát biểu của chủ nghĩa Khắc kỷ như thế nào. Tuy nhiên, đối tượng độc giả chính mà tôi hướng đến là những cá nhân bình thường, những người băn khoăn không biết bản thân có đang sống lầm lỗi hay không. Đối tượng này gồm những người dần nhận ra rằng họ thiếu một triết lý sống nhất quán và hậu quả là họ đang lúng túng trong các hoạt động thường ngày của mình: những thành quả họ đạt được ngày hôm nay lại phủi sạch những thành quả đã đạt được ngày hôm trước. Đồng thời, tôi cũng hướng đến những người đã có một triết lý sống nhưng lo ngại rằng triết lý đó phần nào khiếm khuyết.\r\n\r\nTrong quá trình viết cuốn sách này, tôi luôn đặt ra trong đầu câu hỏi: Nếu các nhà Khắc kỷ cổ đại đảm nhận trọng trách viết một cuốn sách hướng dẫn cho những người sống ở thế kỷ hai mốt – một cuốn sách chỉ cho chúng ta cách có được một cuộc sống tốt đẹp – thì cuốn sách đó sẽ như thế nào. Các trang tiếp theo là câu trả lời của tôi cho câu hỏi này.” Chia sẻ của tác giả William B.Irvine tới bạn đọc!', 'Tiếng Việt', 2022, 'NXB Công Thương', 200000, 200, 0, 0.8, '1654690866395_8935280911109.jpg', '1654690866398_8935280911109.jpg', 0, '2022-06-08 19:21:06', '2022-06-08 19:33:25');
INSERT INTO `books` VALUES (32, ' Làm Chủ Cuộc Đời', 'Khangser Rinpoche', NULL, 'Trong nhịp sống hối hả của thời đại mà khoa học vật chất không ngừng phát triển, con người sẽ ngày càng có ít thời gian hơn để chăm lo đời sống tinh thần. Chúng ta dành hầu hết thời giờ có được trong ngày cho công việc và những thú vui, song lại hiếm khi dành cho mình những khoảng lặng, để tâm mình bình an thoát khỏi mọi vướng bận, lo toan từ cuộc sống. Quay cuồng trong một vòng xoáy không ngừng, đã bao giờ có ai trong chúng ta tự hỏi, những đấu tranh hằng ngày của chúng ta rốt cuộc là vì cái gì và đâu mới là đích đến? \r\n\r\nCuốn sách “Làm chủ cuộc đời” tập hợp những bài giảng của tiến sỹ Phật học Khangser Rinpoche, diễn giải khái quát lời dạy của đức Phật Thích Ca Mâu Ni, bậc chân tu khám phá ra con đường bền vững đưa con người thoát khỏi bể khổ, gầy dựng hạnh phúc bền lâu.\r\n\r\nCuốn sách mở đầu với những tiền đề căn bản, khẳng định đạo Phật là một môn khoa học về bản tâm con người, chứ không đơn thuần là một tôn giáo. Phần thứ hai trình bày bốn sự thật cao quý, là thành quả từ công trình chiêm nghiệm về kiếp nhân sinh của đức Phật trong suốt sáu năm ròng tu khổ hạnh và là nền tảng của toàn bộ lời Phật dạy. Và tiếp nối cho những chủ đề kế tiếp là những thông điệp sống hạnh phúc, lòng từ bi và trí tuệ trong mỗi người, từ đó ta sẽ biết trân quý từng khoảnh khắc thực tại và nỗ lực sống một cuộc đời đầy ý nghĩa.\r\n\r\nĐối với Khangser Rinpoche mục đích sống của Thầy đó là: “Sống mạnh mẽ và hạnh phúc”. Với tâm từ bi và ước muốn giúp mọi người làm sáng tỏ những hoài nghi trên con đường mưu cầu hạnh phúc và nhìn thấy rõ sự cầu lạc nội tâm trong cuộc đời.\r\n\r\nKhangser Rinpoche đã đến Việt Nam nhiều lần và trong những chuyến thăm trước, Thầy đã nói chuyện với nhiều người thuộc nhiều nghành nghề  và độ tuổi khác nhau từ các doanh nhân, nhân viên, sinh viên tiểu học, sinh viên, giáo viên…Trong các bài giảng,Thầy đã chỉ rõ nhiều phương pháp để chúng ta có thể thực sự sống mạnh mẽ và hạnh phúc. Vì trong mỗi người chúng ta đang thiếu khả năng nhận thức đầy đủ giá trị mình đang có, và ta tìm kiếm hạnh phúc không đúng cách. Bằng cách thực hành theo cách mà Thầy hướng dẫn, chúng ta có thể từng bước phát triển sự tỉnh thức và trưởng dưỡng hạnh phúc nội tâm trong khi vẫn tiếp tục theo đuổi khát vọng của bản thân và hướng đến những gì tốt đẹp trong cuộc sống. \r\n\r\nVới ước mơ của Thầy là nuôi dưỡng hạt giống của an lạc và hạnh phúc thật thụ trong tâm tất cả mọi người. Mong rằng với cuốn sách “Làm chủ cuộc đời” sẽ giúp bạn đọc tìm thấy một điểm tựa tinh thần trong cuộc đời của chính mình.\r\n\r\nMã hàng	8935086841181\r\nTên Nhà Cung Cấp	FIRST NEWS\r\nTác giả	Khangser Rinpoche\r\nNXB	NXB Hồng Đức\r\nNăm XB	10-2016\r\nTrọng lượng (gr)	200\r\nKích Thước Bao Bì	20.5 x 14.5\r\nSố trang	148\r\nHình thức	Bìa Mềm\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Kỹ năng sống bán chạy của tháng\r\nLàm Chủ Cuộc Đời\r\n\r\nTrong nhịp sống hối hả của thời đại mà khoa học vật chất không ngừng phát triển, con người sẽ ngày càng có ít thời gian hơn để chăm lo đời sống tinh thần. Chúng ta dành hầu hết thời giờ có được trong ngày cho công việc và những thú vui, song lại hiếm khi dành cho mình những khoảng lặng, để tâm mình bình an thoát khỏi mọi vướng bận, lo toan từ cuộc sống. Quay cuồng trong một vòng xoáy không ngừng, đã bao giờ có ai trong chúng ta tự hỏi, những đấu tranh hằng ngày của chúng ta rốt cuộc là vì cái gì và đâu mới là đích đến? \r\n\r\nCuốn sách “Làm chủ cuộc đời” tập hợp những bài giảng của tiến sỹ Phật học Khangser Rinpoche, diễn giải khái quát lời dạy của đức Phật Thích Ca Mâu Ni, bậc chân tu khám phá ra con đường bền vững đưa con người thoát khỏi bể khổ, gầy dựng hạnh phúc bền lâu.\r\n\r\nCuốn sách mở đầu với những tiền đề căn bản, khẳng định đạo Phật là một môn khoa học về bản tâm con người, chứ không đơn thuần là một tôn giáo. Phần thứ hai trình bày bốn sự thật cao quý, là thành quả từ công trình chiêm nghiệm về kiếp nhân sinh của đức Phật trong suốt sáu năm ròng tu khổ hạnh và là nền tảng của toàn bộ lời Phật dạy. Và tiếp nối cho những chủ đề kế tiếp là những thông điệp sống hạnh phúc, lòng từ bi và trí tuệ trong mỗi người, từ đó ta sẽ biết trân quý từng khoảnh khắc thực tại và nỗ lực sống một cuộc đời đầy ý nghĩa.\r\n\r\nĐối với Khangser Rinpoche mục đích sống của Thầy đó là: “Sống mạnh mẽ và hạnh phúc”. Với tâm từ bi và ước muốn giúp mọi người làm sáng tỏ những hoài nghi trên con đường mưu cầu hạnh phúc và nhìn thấy rõ sự cầu lạc nội tâm trong cuộc đời.', 'Tiếng Việt', 2020, '	NXB Hồng Đức', 150000, 200, 0, 1, '1654691103659_8935280911109.jpg', '1654691103661_8935280911109.jpg', 0, '2022-06-08 19:24:44', '2022-06-08 19:33:20');
INSERT INTO `books` VALUES (33, 'Giải Toán Cùng Einstein', 'Edward Zaccaro', NULL, 'Mọi học sinh đều cần một bộ công cụ các chiến lược giải toán để dùng đến khi làm toán. Bổ sung cho nhiều cách giải truyền thống, cuốn sách này bao gồm nhiều phương pháp mới như: Nghĩđến số 1, Phương pháp 2-10, và nhiều cách khác do giáo viên dạy toán Edward Zaccaro phát triển. Mỗi bài học bao gồm các bài toán được chia thành 5 trình độ để phù hợp với không chỉ các học sinh trung bình, mà cả những học sinh giỏi. Cuốn sách cũng đưa ra đáp án và cách giải chi tiết để các bậc phụ huynh có thể tham khảo trong quá trình hướng dẫn con em mình.\r\n\r\nCác câu hỏi trong mỗi chương được chia theo 5 trình độ:\r\n\r\nTrình độ 1 (dễ)\r\nTrình độ 2 (khá khó)\r\nTrình độ 3 (khó)\r\nTrình độ Einstein (rất khó)\r\nTrình độ Einstein siêu đẳng (cực khó)\r\n\"Đừng lo lắng về khó khăn của bạn trong toán học, tôi đảm bảo với bạn rằng những khó khăn toán học của tôi còn gấp bội.”\r\n\r\n- Albert Einstein.\r\n\r\nMã hàng	8935251406849\r\nCấp Độ/ Lớp	Lớp 2\r\nCấp Học	Cấp 1\r\nTên Nhà Cung Cấp	Alpha Books\r\nTác giả	Edward Zaccaro\r\nNgười Dịch	Nguyễn Tú\r\nNXB	NXB Thế Giới\r\nNăm XB	2017\r\nNgôn Ngữ	Tiếng Việt\r\nTrọng lượng (gr)	400\r\nKích Thước Bao Bì	24 x 16 cm\r\nSố trang	336\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nAlpha Books\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Tham Khảo Lớp 2 bán chạy của tháng\r\nGiải Toán Cùng Einstein\r\n\r\nMọi học sinh đều cần một bộ công cụ các chiến lược giải toán để dùng đến khi làm toán. Bổ sung cho nhiều cách giải truyền thống, cuốn sách này bao gồm nhiều phương pháp mới như: Nghĩđến số 1, Phương pháp 2-10, và nhiều cách khác do giáo viên dạy toán Edward Zaccaro phát triển. Mỗi bài học bao gồm các bài toán được chia thành 5 trình độ để phù hợp với không chỉ các học sinh trung bình, mà cả những học sinh giỏi. Cuốn sách cũng đưa ra đáp án và cách giải chi tiết để các bậc phụ huynh có thể tham khảo trong quá trình hướng dẫn con em mình.', 'Tiếng Việt', 2017, 'NXB Thế Giới', 59000, 200, 0, 0.9, 'giaitoan.jpg', 'giaitoan.jpg', 0, '2022-06-08 19:27:42', '2022-06-08 19:33:15');
INSERT INTO `books` VALUES (34, 'Từ Điển Tiếng “Em”', '	Khotudien', NULL, 'Ừ ĐIỂN TIẾNG “EM” – Định nghĩa về thế giới mới!\r\n\r\nBạn sẽ bất ngờ, khi cầm cuốn “từ điển” xinh xinh này trên tay.\r\n\r\nVà sẽ còn ngạc nhiên hơn nữa, khi bắt đầu đọc từng trang sách…\r\n\r\nDĩ nhiên là vì “Từ điển tiếng “Em” không phải là một cuốn từ điển thông thường rồi!\r\n\r\nNói đến “từ điển”, xưa nay chúng ta đều nghĩ về một bộ sách đồ sộ, giải thích ý nghĩa, cách dùng, dịch, cách phát âm, và thường kèm theo các ví dụ về cách sử dụng từ đó.\r\n\r\nTuy nhiên, với cuốn sách “Từ điển tiếng “em”, các bạn sẽ hết sức bất ngờ với những định nghĩa mới, bắt trend, sáng tạo, thông minh và vô cùng hài hước.\r\n\r\nTiếng “em” [danh từ] ở đây là tiếng lòng của những người yêu sự thật, ghét sự giả dối\r\n\r\nCô đơn [ tính từ ] không phải là không có ai bên cạnh, mà là người mình muốn ở cạnh lại không hề ở bên\r\n\r\nSống lỗi [ động từ ] là cách sống của mấy bạn có người yêu cái là bỏ bê bạn bè liền hà...\r\n\r\nNhưng đây không chỉ là cuốn sách chỉ biết nói dăm ba câu chuyện về tình yêu.\r\nCòn nhiều hơn thế!\r\n\r\nLà những câu cửa miệng của giới trẻ thời nay; là hoạt động tưởng vô bổ nhưng cần thiết cho sự sống: ăn, ngủ, tắm, gội cũng được định nghĩa hết sức dí dỏm... Và cũng không thiếu những “tật xấu, thói hư” nghĩ đến đã thấy “tức cái lồng ngực”...\r\n\r\nVà bạn yên tâm, “tập đoàn” Kho Từ Điển điều hành bởi Thịt Kho – Hiệp Thị - 2 chủ xị cho ra đời cuốn sách nhỏ bé xíu xiu nhưng mới mẻ và mặn mà vô cùng này sẽ khiến bạn thoát mác “người tối cổ” cười cả ngày không chán, nhìn bạn bè quanh mình bằng ánh mắt dễ thương, tận hưởng cuộc đời với những định nghĩa hoàn toàn!!!\r\n\r\nCuốn sách này giống như một chiếc hộp Pandora thú vị và hấp dẫn người đọc, vì bạn không thể đoán trước được tác giả sẽ “định nghĩa” câu nói đó theo nghĩa nào, cho ta thêm thích thú với những ngôn từ đáng yêu sử dụng mỗi ngày.\r\nVậy nên, ngay bây giờ, bạn đã sẵn sàng để mở ra những điều thú vị trong cuốn sách này chưa!!!\r\n\r\nMã hàng	8935325000980\r\nTên Nhà Cung Cấp	Skybooks\r\nTác giả	Khotudien\r\nNXB	NXB Phụ Nữ Việt Nam\r\nNăm XB	2021\r\nNgôn Ngữ	Tiếng Việt\r\nTrọng lượng (gr)	300\r\nKích Thước Bao Bì	12 x 10 cm\r\nSố trang	280\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nĐồ Chơi Cho Bé - Giá Cực Tốt\r\nFlashsale\r\nMã Giảm Giá\r\nPNJ\r\nVNPAY\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Truyện ngắn - Tản Văn bán chạy của tháng\r\nTỪ ĐIỂN TIẾNG “EM” – Định nghĩa về thế giới mới!\r\n\r\nBạn sẽ bất ngờ, khi cầm cuốn “từ điển” xinh xinh này trên tay.\r\n\r\nVà sẽ còn ngạc nhiên hơn nữa, khi bắt đầu đọc từng trang sách…\r\n\r\nDĩ nhiên là vì “Từ điển tiếng “Em” không phải là một cuốn từ điển thông thường rồi!\r\n\r\nNói đến “từ điển”, xưa nay chúng ta đều nghĩ về một bộ sách đồ sộ, giải thích ý nghĩa, cách dùng, dịch, cách phát âm, và thường kèm theo các ví dụ về cách sử dụng từ đó.\r\n\r\nTuy nhiên, với cuốn sách “Từ điển tiếng “em”, các bạn sẽ hết sức bất ngờ với những định nghĩa mới, bắt trend, sáng tạo, thông minh và vô cùng hài hước.\r\n\r\nTiếng “em” [danh từ] ở đây là tiếng lòng của những người yêu sự thật, ghét sự giả dối\r\n\r\nCô đơn [ tính từ ] không phải là không có ai bên cạnh, mà là người mình muốn ở cạnh lại không hề ở bên\r\n\r\nSống lỗi [ động từ ] là cách sống của mấy bạn có người yêu cái là bỏ bê bạn bè liền hà...\r\n\r\nNhưng đây không chỉ là cuốn sách chỉ biết nói dăm ba câu chuyện về tình yêu.\r\nCòn nhiều hơn thế!\r\n\r\nLà những câu cửa miệng của giới trẻ thời nay; là hoạt động tưởng vô bổ nhưng cần thiết cho sự sống: ăn, ngủ, tắm, gội cũng được định nghĩa hết sức dí dỏm... Và cũng không thiếu những “tật xấu, thói hư” nghĩ đến đã thấy “tức cái lồng ngực”...\r\n\r\nVà bạn yên tâm, “tập đoàn” Kho Từ Điển điều hành bởi Thịt Kho – Hiệp Thị - 2 chủ xị cho ra đời cuốn sách nhỏ bé xíu xiu nhưng mới mẻ và mặn mà vô cùng này sẽ khiến bạn thoát mác “người tối cổ” cười cả ngày không chán, nhìn bạn bè quanh mình bằng ánh mắt dễ thương, tận hưởng cuộc đời với những định nghĩa hoàn toàn!!!\r\n\r\nCuốn sách này giống như một chiếc hộp Pandora thú vị và hấp dẫn người đọc, vì bạn không thể đoán trước được tác giả sẽ “định nghĩa” câu nói đó theo nghĩa nào, cho ta thêm thích thú với những ngôn từ đáng yêu sử dụng mỗi ngày.\r\nVậy nên, ngay bây giờ, bạn đã sẵn sàng để mở ra những điều thú vị trong cuốn sách này chưa!!!', 'Tiếng Việt', 2021, '	NXB Phụ Nữ Việt Nam', 69000, 200, 0, 1, 'em.jpg', 'em.jpg', 0, '2022-06-08 19:33:06', '2022-06-08 19:34:43');
INSERT INTO `books` VALUES (35, 'Gà Béo Đang Bay', 'Fat Chick', NULL, NULL, 'Tiếng Việt', 2019, 'NXB Hà Nội', 60000, 200, 0, 1, 'ga.jpg', 'ga.jpg', 0, '2022-06-08 19:35:37', '2022-06-08 19:35:56');
INSERT INTO `books` VALUES (36, 'Tình Và Rác', '	Ivan Klíma', NULL, 'Cuộc đời chẳng bao giờ đơn giản kể cả đối với một người đàn ông làm công việc dọn rác trên đường phố Prague. Ông ta vừa là một công nhân thuộc đội vệ sinh đường phố vừa là một nhà văn từng có tên tuổi rực rỡ. Một người chồng, người cha với hai đứa con đã lớn khôn vừa là một người tình đầy đam mê, vừa là chứng nhân của lịch sử vừa là kẻ bên lề của cuộc sống hiện tại... Nhân vật đầy những mâu thuẫn ấy là người kể chuyện của “Tình và rác” của Ivan Klíma. Xuyên suốt câu chuyện được kể lại trong tác phẩm, chúng ta được chứng kiến cuộc sống, thân phận của những người tri thức Do Thái trong dòng xoáy nghiệt ngã của lịch sử. Nhưng điều tuyệt vời hơn cả của tác phẩm đó là đã cho người đọc thấy những suy tưởng tuyệt đẹp của tác giả về văn chương nghệ thuật và tình yêu...\r\n\r\nVới lối kể chuyện đan xen nhiều tình tiết, nhiều nhân vật trong những địa điểm và thời gian hoàn toàn khác nhau, tiểu thuyết “Tình và rác” của nhà văn Séc - Ivan Klíma đã cuốn hút người đọc khắp nơi trên thế giới không phải bởi những gì ly kỳ, bí ẩn hay mạo hiểm giữa sự sống với cái chết mà bởi mạch suy tưởng lẫn lộn giữa thực và hư, giữa những gì đang xảy ra với những gì đã xảy ra, giữa những con người có thực này với những con người đó như ở hoàn cảnh không có thực khác. Ivan Klíma xứng đáng là một đại diện xuất sắc của văn học Séc bên cạnh những gương mặt quen thuộc như Franz Kafka hay Milan Kundera.\r\n\r\nThông tin tác giả:\r\n\r\nIvan Klíma sinh năm 1938 là một nhà văn người Séc. Ông mang trong mình ký ức của những người Do Thái bị chà đạp trong thế chiến thứ hai ở Châu Âu, điều đó đã ảnh hưởng rất nhiều đến những tác phẩm của ông.\r\n\r\nCác tác phẩm:\r\n\r\n- Love and Garbage\r\n\r\n- Judge on Trial\r\n\r\n- My Golden Trades\r\n\r\n- Lovers for a Day\r\n\r\n- My Mad Century\r\n\r\nMã hàng	9786048888367\r\nTên Nhà Cung Cấp	Bách Việt\r\nTác giả	Ivan Klíma\r\nNgười Dịch	Bạch Phượng\r\nNXB	NXB Dân Trí\r\nNăm XB	2020\r\nTrọng lượng (gr)	500\r\nKích Thước Bao Bì	13 x 20.5 cm\r\nSố trang	480\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nBách Việt\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Tiểu thuyết bán chạy của tháng\r\nCuộc đời chẳng bao giờ đơn giản kể cả đối với một người đàn ông làm công việc dọn rác trên đường phố Prague. Ông ta vừa là một công nhân thuộc đội vệ sinh đường phố vừa là một nhà văn từng có tên tuổi rực rỡ. Một người chồng, người cha với hai đứa con đã lớn khôn vừa là một người tình đầy đam mê, vừa là chứng nhân của lịch sử vừa là kẻ bên lề của cuộc sống hiện tại... Nhân vật đầy những mâu thuẫn ấy là người kể chuyện của “Tình và rác” của Ivan Klíma. Xuyên suốt câu chuyện được kể lại trong tác phẩm, chúng ta được chứng kiến cuộc sống, thân phận của những người tri thức Do Thái trong dòng xoáy nghiệt ngã của lịch sử. Nhưng điều tuyệt vời hơn cả của tác phẩm đó là đã cho người đọc thấy những suy tưởng tuyệt đẹp của tác giả về văn chương nghệ thuật và tình yêu...\r\n\r\nVới lối kể chuyện đan xen nhiều tình tiết, nhiều nhân vật trong những địa điểm và thời gian hoàn toàn khác nhau, tiểu thuyết “Tình và rác” của nhà văn Séc - Ivan Klíma đã cuốn hút người đọc khắp nơi trên thế giới không phải bởi những gì ly kỳ, bí ẩn hay mạo hiểm giữa sự sống với cái chết mà bởi mạch suy tưởng lẫn lộn giữa thực và hư, giữa những gì đang xảy ra với những gì đã xảy ra, giữa những con người có thực này với những con người đó như ở hoàn cảnh không có thực khác. Ivan Klíma xứng đáng là một đại diện xuất sắc của văn học Séc bên cạnh những gương mặt quen thuộc như Franz Kafka hay Milan Kundera.\r\n\r\nThông tin tác giả:\r\n\r\nIvan Klíma sinh năm 1938 là một nhà văn người Séc. Ông mang trong mình ký ức của những người Do Thái bị chà đạp trong thế chiến thứ hai ở Châu Âu, điều đó đã ảnh hưởng rất nhiều đến những tác phẩm của ông.', 'Tiếng Việt', 2020, 'NXB Dân Trí', 50000, 200, 0, 1, 'tinhvarac.jpg', 'tinhvarac.jpg', 0, '2022-06-08 19:37:04', '2022-06-08 19:38:03');
INSERT INTO `books` VALUES (37, 'Dại Thì Chết', 'Mario Puzo', NULL, 'Dại thì chết là tiểu thuyết đầu tiên của Mario Puzo từ sau Bố già, và ra đời sau Bố già đến chín năm. Bản quyền in sách bìa mềm của cuốn này được bán với giá hai triệu năm mươi lăm ngàn đô-la, một con số kỷ lục thời ấy.\r\n\r\nBố già được viết hoàn toàn dựa trên trí tưởng tượng và tư liệu nghiên cứu, còn Dại thì chết chủ yếu dựa trên kinh nghiệm thực tế. Bản thân Puzo là nhà văn và nhà biên kịch, lại rất máu mê đánh bài, nên ông lấy văn chương, điện ảnh, và cờ bạc làm chủ đề cho tiểu thuyết này. Nhiều nhân vật trong Dại thì chết được xây dựng trên nguyên mẫu có thật ngoài đời. Bối cảnh tiểu thuyết là ba kinh đô của nước Mỹ: kinh đô văn chương New York, kinh đô điện ảnh Hollywood, và kinh đô cờ bạc Las Vegas. Những chuyện hậu trường văn chương, điện ảnh, và những mánh mung của làng đỏ đen, tất cả đều được Puzo phơi bày với tư cách một người trong cuộc.”\r\n\r\nGiới thiệu tác giả: Mario Puzo (1920 - 1999) là nhà văn, nhà biên kịch người Mỹ. Ông sinh ra trong một gia đình nhập cư nghèo gốc Napoli, sống tại New York. Khi Thế chiến II bùng nổ, ở tuổi đôi mươi, Puzo nhiệt thành đăng lính. Chiến tranh kết thúc, Puzo trở về lập gia đình và tiếp tục con đường học vấn. Năm 1950, ông ra mắt truyện ngắn đầu tay The Last Christmas. Năm năm sau, ông xuất bản cuốn sách đầu tiên, The Dark Arena, trong lúc đang đảm nhiệm vị trí biên tập viên cho nhà xuất bản Martin Goodman. Thời gian này, ông còn tham gia viết bài cho một số tạp chí dành cho đàn ông như Male, True Action và Swank. Năm 1965, ông xuất bản The Fortunate Pilgrim. Năm 1969, tiểu thuyết The Godfather, tác phẩm nổi tiếng nhất của Puzo, ra đời và gây tiếng vang lớn trên văn đàn thế giới, nhanh chóng được chuyển thể thành phim. Mario Puzo tiếp tục phát triển sự nghiệp văn chương và xuất bản nhiều tiểu thuyết ăn khách như Fools Die (1978), The Sicilian (1984), The Fourth K (1990), The Last Don (1996). Năm 1999, ông đột ngột qua đời sau một cơn trụy tim. Hai bản thảo cuối cùng của Puzo được xuất bản sau khi ông mất là Omertà (2000) và The Family (2001).\r\n\r\nNhận xét về tác phẩm:\r\n\r\n“Không kiêng dè, dữ dội mà thú vị... Puzo đã mang độc giả vào sòng bài, thậm chí là vào sau hậu trường, vào trong hộp bài và bàn thu ngân, tiến vào cuộc đời của những kẻ thua bạc.”\r\n\r\n- Abilene Reporter News\r\n\r\n“Đầy hấp dẫn... Xuất sắc... Đây là Puzo ở đỉnh cao phong độ... Một thế giới sôi nổi của bài bạc và của những tên điếm thần sầu, cả đàn ông lẫn phụ nữ, thú vị không kém gì Bố già, và khủng khiếp cũng không kém.”\r\n\r\n- Publishers Weekly\r\n\r\n“Tiền tài và danh vọng, lừa bịp và dối gian và lợi dụng, yêu đương và mua bán xác thân, cảnh này nối đuôi cảnh lộn xộn khác, tất cả đều được viết với sức sống không bao giờ lụi tàn... Chỉ những độc giả thờ ơ mới có thể không thiết ngấu nghiến tất cả những thứ này.”\r\n\r\n- The New York Times Book Review\r\n\r\nMã hàng	8936203361391\r\nTên Nhà Cung Cấp	Đông A\r\nTác giả	Mario Puzo\r\nNgười Dịch	Nguyễn Minh\r\nNXB	NXB Văn Học\r\nNăm XB	2022\r\nTrọng lượng (gr)	900\r\nKích Thước Bao Bì	24 x 16 x 1.3 cm\r\nSố trang	780\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nĐông A\r\nSách Mới\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Tiểu thuyết bán chạy của tháng\r\nDại Thì Chết\r\n\r\n“Dại thì chết là tiểu thuyết đầu tiên của Mario Puzo từ sau Bố già, và ra đời sau Bố già đến chín năm. Bản quyền in sách bìa mềm của cuốn này được bán với giá hai triệu năm mươi lăm ngàn đô-la, một con số kỷ lục thời ấy.\r\n\r\nBố già được viết hoàn toàn dựa trên trí tưởng tượng và tư liệu nghiên cứu, còn Dại thì chết chủ yếu dựa trên kinh nghiệm thực tế. Bản thân Puzo là nhà văn và nhà biên kịch, lại rất máu mê đánh bài, nên ông lấy văn chương, điện ảnh, và cờ bạc làm chủ đề cho tiểu thuyết này. Nhiều nhân vật trong Dại thì chết được xây dựng trên nguyên mẫu có thật ngoài đời. Bối cảnh tiểu thuyết là ba kinh đô của nước Mỹ: kinh đô văn chương New York, kinh đô điện ảnh Hollywood, và kinh đô cờ bạc Las Vegas. Những chuyện hậu trường văn chương, điện ảnh, và những mánh mung của làng đỏ đen, tất cả đều được Puzo phơi bày với tư cách một người trong cuộc.”\r\n\r\nGiới thiệu tác giả: Mario Puzo (1920 - 1999) là nhà văn, nhà biên kịch người Mỹ. Ông sinh ra trong một gia đình nhập cư nghèo gốc Napoli, sống tại New York. Khi Thế chiến II bùng nổ, ở tuổi đôi mươi, Puzo nhiệt thành đăng lính. Chiến tranh kết thúc, Puzo trở về lập gia đình và tiếp tục con đường học vấn. Năm 1950, ông ra mắt truyện ngắn đầu tay The Last Christmas. Năm năm sau, ông xuất bản cuốn sách đầu tiên, The Dark Arena, trong lúc đang đảm nhiệm vị trí biên tập viên cho nhà xuất bản Martin Goodman. Thời gian này, ông còn tham gia viết bài cho một số tạp chí dành cho đàn ông như Male, True Action và Swank. Năm 1965, ông xuất bản The Fortunate Pilgrim. Năm 1969, tiểu thuyết The Godfather, tác phẩm nổi tiếng nhất của Puzo, ra đời và gây tiếng vang lớn trên văn đàn thế giới, nhanh chóng được chuyển thể thành phim. Mario Puzo tiếp tục phát triển sự nghiệp văn chương và xuất bản nhiều tiểu thuyết ăn khách như Fools Die (1978), The Sicilian (1984), The Fourth K (1990), The Last Don (1996). Năm 1999, ông đột ngột qua đời sau một cơn trụy tim. Hai bản thảo cuối cùng của Puzo được xuất bản sau khi ông mất là Omertà (2000) và The Family (2001).', 'Tiếng Việt', 2018, 'NXB Văn Học', 250000, 100, 0, 1, 'ngu.jpg', 'ngu.jpg', 0, '2022-06-08 19:39:56', '2022-06-08 19:39:56');
INSERT INTO `books` VALUES (38, 'Hãng Luật - The Firm', '	John Grisham', NULL, 'Khi luật sư trẻ Mitch McDeere ký hợp đồng với Hãng Luật Bendini, Lambert & Locke của Memphis, anh đã nghĩ mình đang chạm đến một giấc mơ tươi đẹp. Công ty hoàn hảo, tiền tài và danh vọng đều trong tầm tay. Lẽ ra, anh phải nhớ nằm lòng một điều: chẳng có gì là miễn phí. Hạnh phúc không bao giờ từ trên trời rơi xuống mà luôn có một cái giá rất đắt. \r\nVà bây giờ, FBI đang đề nghị Mitch McDeere giúp đỡ điều tra. Nếu muốn sống, anh chỉ có một lựa chọn duy nhất…\r\n\r\nThe Firm – Hãng Luật là một siêu phẩm của John Grisham. Ngay khi ra mắt lần đầu vào năm 1991, tác phẩm đã nhanh chóng trở thành bestseller, đứng trong danh sách bán chạy của The New York Times  47 tuần và bán ra hơn bảy triệu bản. Cuốn sách được chuyển thể thành bộ phim cùng tên ra mắt vào năm 1993 với sự tham gia diễn xuất của ngôi sao Tom Cruise, cũng như một loạt phim truyền hình vào năm 2012 với nội dung là \"tiếp tục câu chuyện của luật sư Mitchell McDeere và gia đình anh 10 năm sau.\"', 'Tiếng Việt', 2019, 'NXB Thế giới', 217000, 100, 0, 1, 'law.jpg', 'law.jpg', 0, '2022-06-08 19:41:41', '2022-06-08 19:41:41');
INSERT INTO `books` VALUES (39, 'The Broker', 'John Grisham', NULL, 'n his final hours in the Oval Office, the outgoing President grants a controversial last-minute pardon to Joel Backman, a notorious Washington power broker who has spent the last six years hidden away in a federal prison. What no one knows is that the President issues the pardon only after receiving enormous pressure from the CIA. It seems Backman, in his power broker heyday, may have obtained secrets that compromise the world s most sophisticated satellite surveillance system.\r\n\r\nBackman is quietly smuggled out of the country in a military cargo plane, given a new name, a new identity, and a new home in Italy. Eventually, after he has settled into his new life, the CIA will leak his whereabouts to the Israelis, the Russians, the Chinese, and the Saudis. Then the CIA will do what it does best: sit back and watch. The question is not whether Backman will survive there is no chance of that. The question the CIA needs answered is, who will kill him?\r\n\r\nholic Takes Manhattan—but will she have to return it?\r\n\r\n> on of exquisite lovemaking. Now the unwed single mother and war-scarred veteran must make a decision that could forever alter their lives. For Sydnam, it is a chance to heal the pain of the past. For Anne, it is the glorious promise of a future with the man who will dare her to reveal her deepest secrets…before she can give him all her heart.\r\n\r\nrelationship shatters and another begins, Sarah makes a series of powerful discoveries: about the true meaning of a dying man s last gift...about the extraordinary legacies that are passed from generation to generation...and about a future she s only just beginning to imagine.\r\n\r\nIn a novel of daring and hope, of embracing life and taking chances, Danielle Steel brilliantly captures one woman s courageous choice to pour herself into a dream and receive its gifts in return.\r\n\r\n \r\n\r\n \r\n\r\nMã hàng	9780440241584-tl\r\nNhà Cung Cấp	Random House\r\nTác giả	John Grisham\r\nNXB	Random House\r\nNăm XB	2005\r\nTrọng lượng (gr)	180\r\nKích Thước Bao Bì	17.526x10.668\r\nSố trang	422\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nPenguin Books\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Political & Legal bán chạy của tháng\r\nSản phẩm có tình trạng chất lượng tương đương 80% so với hàng mới.\r\n\r\nLưu ý: Các sản phẩm thuộc \'Phiên chợ sách cũ\' sẽ không được áp dụng chính sách đổi trả của Fahasa.com\r\n\r\nIn his final hours in the Oval Office, the outgoing President grants a controversial last-minute pardon to Joel Backman, a notorious Washington power broker who has spent the last six years hidden away in a federal prison. What no one knows is that the President issues the pardon only after receiving enormous pressure from the CIA. It seems Backman, in his power broker heyday, may have obtained secrets that compromise the world s most sophisticated satellite surveillance system.\r\n\r\nBackman is quietly smuggled out of the country in a military cargo plane, given a new name, a new identity, and a new home in Italy. Eventually, after he has settled into his new life, the CIA will leak his whereabouts to the Israelis, the Russians, the Chinese, and the Saudis. Then the CIA will do what it does best: sit back and watch. The question is not whether Backman will survive there is no chance of that. The question the CIA needs answered is, who will kill him?', 'Tiếng Anh', 2017, 'NXB Thế giới', 67400, 100, 0, 1, 'broken.jpg', 'broken.jpg', 0, '2022-06-08 19:43:31', '2022-06-08 19:43:31');
INSERT INTO `books` VALUES (40, 'The Lucky One', 'Nicholas Sparks', NULL, 'In his 14th book, bestselling author Nicholas Sparks tells the unforgettable story of a man whose brushes with death lead him to the love of his life. After U.S. Marine Logan Thibault finds a photograph of a smiling young woman buried in the dirt during his tour of duty in Iraq, he experiences a sudden streak of luck -- winning poker games and even surviving deadly combat. Only his best friend, Victor, seems to have an explanation for his good fortune: the photograph -- his lucky charm. Back home in Colorado, Thibault can\'t seem to get the woman in the photograph out of his mind and he sets out on a journey across the country to find her. But Thibault is caught off guard by the strong attraction he feels for the woman he encounters in North Carolina - Elizabeth, a divorced mother -- and he keeps the story of the photo, and his luck, a secret. As he and Elizabeth embark upon a passionate love affair, his secret soon threatens to tear them apart -- destroying not only their love, but also their lives. Filled with tender romance and terrific suspense, THE LUCKY ONE is an unforgettable story about the surprising paths our lives often take and the power of fate to guide us to true and everlasting love.\r\n\r\nMã hàng	9781455508976-tl\r\nTên Nhà Cung Cấp	Hachette Book Group\r\nTác giả	Nicholas Sparks\r\nNXB	Hachette\r\nNăm XB	01/03/2012\r\nTrọng lượng (gr)	300\r\nKích Thước Bao Bì	17.0 x 10.4 x 2.8\r\nSố trang	400\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nHachette Book Group\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Romance bán chạy của tháng\r\nSản phẩm có tình trạng chất lượng tương đương 80% so với hàng mới.\r\n\r\nLưu ý: Các sản phẩm thuộc \'Phiên chợ sách cũ\' sẽ không được áp dụng chính sách đổi trả của Fahasa.com\r\n\r\nIn his 14th book, bestselling author Nicholas Sparks tells the unforgettable story of a man whose brushes with death lead him to the love of his life. After U.S. Marine Logan Thibault finds a photograph of a smiling young woman buried in the dirt during his tour of duty in Iraq, he experiences a sudden streak of luck -- winning poker games and even surviving deadly combat. Only his best friend, Victor, seems to have an explanation for his good fortune: the photograph -- his lucky charm. Back home in Colorado, Thibault can\'t seem to get the woman in the photograph out of his mind and he sets out on a journey across the country to find her. But Thibault is caught off guard by the strong attraction he feels for the woman he encounters in North Carolina - Elizabeth, a divorced mother -- and he keeps the story of the photo, and his luck, a secret. As he and Elizabeth embark upon a passionate love affair, his secret soon threatens to tear them apart -- destroying not only their love, but also their lives. Filled with tender romance and terrific suspense, THE LUCKY ONE is an unforgettable story about the surprising paths our lives often take and the power of fate to guide us to true and everlasting love.', 'Tiếng Anh', 2015, 'NXB Thế giới', 78900, 100, 0, 0.5, 'love.jpg', 'love.jpg', 0, '2022-06-08 19:45:14', '2022-06-08 19:45:14');
INSERT INTO `books` VALUES (41, 'The Last Song', '	Nicholas Sparks', NULL, 'Seventeen year old Veronica \"Ronnie\" Miller\'s life was turned upside-down when her parents divorced and her father moved from New York City to Wrightsville Beach, North Carolina. Three years later, she remains angry and alientated from her parents, especially her father...until her mother decides it would be in everyone\'s best interest if she spent the summer in Wilmington with him. Ronnie\'s father, a former concert pianist and teacher, is living a quiet life in the beach town, immersed in creating a work of art that will become the centerpiece of a local church.\r\n\r\nThe tale that unfolds is an unforgettable story of love on many levels--first love, love between parents and children -- that demonstrates, as only a Nicholas Sparks novel can, the many ways that love can break our hearts...and heal them.', 'Tiếng Anh', 2010, 'NXB Thế giới', 85200, NULL, NULL, 1, 'song.jpg', 'song.jpg', 0, '2022-06-08 19:46:56', '2022-06-08 19:46:56');
INSERT INTO `books` VALUES (42, 'Kẻ Giấu Mặt', '	Nicholas Sparks', NULL, 'Cuộc đời mỗi người có thể không tránh khỏi những ngày tang thương ảm đạm, nhưng cũng có những ngày hoàn hào, ngày huy hoàng lộng lẫy, khi mọi thứ dường như đều khả dĩ. Cũng như Colin đã vùng vẫy để bỏ lại sau lưng một quá khứ bạo lực và những câu chuyện không vừa ý, để thận trọng nhận lấy cơ hội thứ hai làm lại cuộc đời: thậm chí còn đánh liều bước vào một mối quan hệ mới mẻ với Maria, cô gái tình cờ gặp gỡ trên con đường mưa.\r\n\r\nNhưng bóng ma quá khứ của Maria, đột ngột trườn đến như một con rắn trong đêm, mang theo thù hận hiểm độc, đã thách thức tất cả những giả định ban đầu của hai người về nhau và về chính mình.\r\n\r\nKhông như những tiểu thuyết thuần túy lãng mạn của Nicholas Sparks trước đây, Kẻ Giấu Mặt căng thẳng tột độ và kịch tính nghẹt tim nhưng cũng giàu lòng tin tưởng. Như một đêm mưa giông trên con đường lầy lội vẫn hứa hẹn sớm mai dịu ngọt, rạng ngời.\r\n\r\n\"Sparks giờ đây không chỉ là tác giả ăn khách nữa, ông đã trở thành một hiện tượng văn học đại chúng.\" - Irish Independent\r\n\r\n\"Mỗi khi cầm lên một cuốn sách của Nicholas Sparks, bạn biết mình đã được dẫn lối vào một chuyện tình đẹp níu giữ trái tim và đôi mắt bạn đến tận trang cuối cùng.\" - Woman\'s Day\r\n\r\nMã hàng	8935235218246\r\nTên Nhà Cung Cấp	Nhã Nam\r\nTác giả	Nicholas Sparks\r\nNgười Dịch	L T K\r\nNXB	NXB Hội Nhà Văn\r\nNăm XB	2018\r\nTrọng lượng (gr)	650\r\nKích Thước Bao Bì	14 x 20.5\r\nSố trang	626\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nNhã Nam\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Truyện Trinh Thám - Kiếm Hiệp bán chạy của tháng\r\nCuộc đời mỗi người có thể không tránh khỏi những ngày tang thương ảm đạm, nhưng cũng có những ngày hoàn hào, ngày huy hoàng lộng lẫy, khi mọi thứ dường như đều khả dĩ. Cũng như Colin đã vùng vẫy để bỏ lại sau lưng một quá khứ bạo lực và những câu chuyện không vừa ý, để thận trọng nhận lấy cơ hội thứ hai làm lại cuộc đời: thậm chí còn đánh liều bước vào một mối quan hệ mới mẻ với Maria, cô gái tình cờ gặp gỡ trên con đường mưa.\r\n\r\nNhưng bóng ma quá khứ của Maria, đột ngột trườn đến như một con rắn trong đêm, mang theo thù hận hiểm độc, đã thách thức tất cả những giả định ban đầu của hai người về nhau và về chính mình.\r\n\r\nKhông như những tiểu thuyết thuần túy lãng mạn của Nicholas Sparks trước đây, Kẻ Giấu Mặt căng thẳng tột độ và kịch tính nghẹt tim nhưng cũng giàu lòng tin tưởng. Như một đêm mưa giông trên con đường lầy lội vẫn hứa hẹn sớm mai dịu ngọt, rạng ngời.\r\n\r\n\"Sparks giờ đây không chỉ là tác giả ăn khách nữa, ông đã trở thành một hiện tượng văn học đại chúng.\" - Irish Independent\r\n\r\n\"Mỗi khi cầm lên một cuốn sách của Nicholas Sparks, bạn biết mình đã được dẫn lối vào một chuyện tình đẹp níu giữ trái tim và đôi mắt bạn đến tận trang cuối cùng.\" - Woman\'s Day', 'Tiếng Việt', 2018, '	NXB Hội Nhà Văn', 72900, 100, 0, 1, 'face.jpg', 'face.jpg', 0, '2022-06-08 19:47:51', '2022-06-08 19:48:32');
INSERT INTO `books` VALUES (43, 'Yêu Đi Rồi Khóc', 'Hamlet Trương', NULL, 'Cuốn sách này là một \"bữa tiệc\" thực sự, bạn sẽ có rất nhiều \"món ngon\" để lựa chọn. Yêu thương có, giận hờn có, lãng mạn có, lạnh lùng có, một chút tâm linh cũng có luôn!\r\n\r\nTôi với Tango Trần là bạn thân, chơi với nhau ngót nghét mười lăm năm từ lúc học cấp I đến tận bây giờ. Tôi vô tư vô tâm, còn cô gái này thì kỹ lưỡng chu đáo. Lúc tôi than thở chuyện tiền nong thì nàng đã an tâm với mấy cuốn sổ tiết kiệm trong ngân hàng. Lúc tôi suốt ngày bi bô tiếng Anh trình độ Kitty thì nàng đã vi vu tít ở châu Âu. Lúc tôi ngồi nói xàm nói nhảm cười hô hô thì nàng đã đường hoàng ngồi một chỗ rất đẹp trong một công ty truyền thông. Sau mười lăm năm, tôi đã rút ra kết luận: “Nhỏ này rất nguy hiểm!” Cho nên mặc dầu khá hoảng loạn khi phát hiện ra nàng có thể viết văn thì tôi cũng được cái an ủi là nàng hoàn toàn nguy hiểm đúng như những gì tôi rút tỉa.\r\n\r\nTôi gặp Hàn Vi với một cái duyên kỳ lạ. Nàng ngỏ ý làm cùng tôi khâu kịch bản cho chương trình Radio Online \"Sổ tay cảm xúc\" mà tôi đang làm host. Đúng lúc đó tôi đang loay hoay với quá nhiều thứ cần dọn dẹp trong cuộc đời xoàng xĩnh của mình. Tôi \"ô kê\", chấp nhận để nàng cứu vớt đời mình. Sau đó nàng cho tôi xem truyện ngắn, giới thiệu này nọ nọ kia về chúng. Chúng tôi diễn đọc trên Radio Online nhẹ nhàng, cho đến khi nhiều người nhắn tin facebook cho tôi theo kiểu muốn truy cùng đuổi tận \"Hàn Vi là ai?\" để làm một việc vô cùng bức thiết và thô bạo đó là… add facebook! Tôi đã mời nàng vào cuốn sách này như thế đó!\r\n\r\nVõ Vi Vân có một giọng \"nhề nhệ\" đặc trưng. Mỗi lần nàng nói chuyện điện thoại thì sự nữ tính đó tràn qua loa, ngập lênh láng và làm tê liệt mọi giác quan. Nàng đang là một biên tập viên cho các chương trình truyền hình và tình cờ tiết lộ thói quen \"viết truyện rồi giấu biệt\". Tôi không bỏ qua, tôi nhất quyết bắt nàng lôi hết tất cả ra, phơi bày ra ánh sáng cái \"chất\" của nàng. Nàng giận lắm! Nàng lắc đầu, giậm chân, kiên quyết chỉ thẳng vào mặt tôi và bảo: ô kê anh!\r\n\r\nYêu đi rồi khóc cũng là lần đầu chúng tôi cùng nhau kết hợp, dùng truyện ngắn để mang đến cho các bạn xúc cảm dạt dào của thứ quà tặng lung linh kỳ ảo nhất cuộc đời: Tình yêu. \"Khóc\" trong Yêu đi rồi khóc rất đa nghĩa nhé! Có thể ta khóc vì buồn, vì y lừa dối, nhưng cũng có khi ta khóc vì ta quá hạnh phúc, hay một phút giây nào đó nhận ra mình đã yêu thương một người hơn chính bản thân mình!\r\n\r\nMã hàng	8935212328456\r\nTên Nhà Cung Cấp	Đinh Tị\r\nTác giả	Hamlet Trương\r\nNXB	NXB Văn Học\r\nNăm XB	2019\r\nTrọng lượng (gr)	420\r\nKích Thước Bao Bì	13 x 20.5\r\nSố trang	416\r\nHình thức	Bìa Mềm\r\nSản phẩm hiển thị trong	\r\nĐinh Tị\r\nHamlet Trương\r\nCác Tác Giả Trẻ Được Yêu Thích\r\nSản phẩm bán chạy nhất	Top 100 sản phẩm Truyện ngắn - Tản Văn bán chạy của tháng\r\nCuốn sách này là một \"bữa tiệc\" thực sự, bạn sẽ có rất nhiều \"món ngon\" để lựa chọn. Yêu thương có, giận hờn có, lãng mạn có, lạnh lùng có, một chút tâm linh cũng có luôn!\r\n\r\nTôi với Tango Trần là bạn thân, chơi với nhau ngót nghét mười lăm năm từ lúc học cấp I đến tận bây giờ. Tôi vô tư vô tâm, còn cô gái này thì kỹ lưỡng chu đáo. Lúc tôi than thở chuyện tiền nong thì nàng đã an tâm với mấy cuốn sổ tiết kiệm trong ngân hàng. Lúc tôi suốt ngày bi bô tiếng Anh trình độ Kitty thì nàng đã vi vu tít ở châu Âu. Lúc tôi ngồi nói xàm nói nhảm cười hô hô thì nàng đã đường hoàng ngồi một chỗ rất đẹp trong một công ty truyền thông. Sau mười lăm năm, tôi đã rút ra kết luận: “Nhỏ này rất nguy hiểm!” Cho nên mặc dầu khá hoảng loạn khi phát hiện ra nàng có thể viết văn thì tôi cũng được cái an ủi là nàng hoàn toàn nguy hiểm đúng như những gì tôi rút tỉa.\r\n\r\nTôi gặp Hàn Vi với một cái duyên kỳ lạ. Nàng ngỏ ý làm cùng tôi khâu kịch bản cho chương trình Radio Online \"Sổ tay cảm xúc\" mà tôi đang làm host. Đúng lúc đó tôi đang loay hoay với quá nhiều thứ cần dọn dẹp trong cuộc đời xoàng xĩnh của mình. Tôi \"ô kê\", chấp nhận để nàng cứu vớt đời mình. Sau đó nàng cho tôi xem truyện ngắn, giới thiệu này nọ nọ kia về chúng. Chúng tôi diễn đọc trên Radio Online nhẹ nhàng, cho đến khi nhiều người nhắn tin facebook cho tôi theo kiểu muốn truy cùng đuổi tận \"Hàn Vi là ai?\" để làm một việc vô cùng bức thiết và thô bạo đó là… add facebook! Tôi đã mời nàng vào cuốn sách này như thế đó!\r\n\r\nVõ Vi Vân có một giọng \"nhề nhệ\" đặc trưng. Mỗi lần nàng nói chuyện điện thoại thì sự nữ tính đó tràn qua loa, ngập lênh láng và làm tê liệt mọi giác quan. Nàng đang là một biên tập viên cho các chương trình truyền hình và tình cờ tiết lộ thói quen \"viết truyện rồi giấu biệt\". Tôi không bỏ qua, tôi nhất quyết bắt nàng lôi hết tất cả ra, phơi bày ra ánh sáng cái \"chất\" của nàng. Nàng giận lắm! Nàng lắc đầu, giậm chân, kiên quyết chỉ thẳng vào mặt tôi và bảo: ô kê anh!\r\n\r\nYêu đi rồi khóc cũng là lần đầu chúng tôi cùng nhau kết hợp, dùng truyện ngắn để mang đến cho các bạn xúc cảm dạt dào của thứ quà tặng lung linh kỳ ảo nhất cuộc đời: Tình yêu. \"Khóc\" trong Yêu đi rồi khóc rất đa nghĩa nhé! Có thể ta khóc vì buồn, vì y lừa dối, nhưng cũng có khi ta khóc vì ta quá hạnh phúc, hay một phút giây nào đó nhận ra mình đã yêu thương một người hơn chính bản thân mình!', 'Tiếng Việt', 2019, 'NXB Văn Học', 50000, 100, 0, 1, 'yeudiroikhoc.jpg', 'yeudiroikhoc.jpg', 0, '2022-06-08 19:50:19', '2022-06-08 19:50:35');

-- ----------------------------
-- Table structure for categories
-- ----------------------------
DROP TABLE IF EXISTS `categories`;
CREATE TABLE `categories`  (
  `catId` int(11) NOT NULL AUTO_INCREMENT,
  `catName` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `thumbnails` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `isDisable` tinyint(4) NULL DEFAULT 0,
  `createAt` datetime(0) NULL DEFAULT current_timestamp(0),
  `updateAt` datetime(0) NULL DEFAULT current_timestamp(0) ON UPDATE CURRENT_TIMESTAMP(0),
  PRIMARY KEY (`catId`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

