-- ----------------------------
-- Table structure for rating
-- ----------------------------
DROP TABLE IF EXISTS `rating`;
CREATE TABLE `rating`  (
  `rateId` int(11) NOT NULL AUTO_INCREMENT,
  `bookId` int(11) NULL DEFAULT NULL,
  `userId` int(11) NULL DEFAULT NULL,
  `rate` float NULL DEFAULT NULL,
